import React, { useState } from 'react';
import './Articles.css';

const Article = () => {
  // State to manage the list of articles
  const [articles, setArticles] = useState([
    {
      title: 'Benefits of Proper Nutrition',
      body: 'Proper nutrition is essential for maintaining a healthy body. A balanced diet can prevent chronic diseases and help with weight management...',
      category: 'Nutrition',
      date: '2024-10-13',
      isExpanded: false,
    },
    {
      title: 'Effective Home Workout Routines',
      body: 'Home workouts can be as effective as gym sessions. With the right plan, you can work out and stay fit without leaving your home...',
      category: 'Workout',
      date: '2024-10-12',
      isExpanded: false,
    }
  ]);
  
  // State for managing the form inputs for a new article
  const [title, setTitle] = useState('');
  const [body, setBody] = useState('');
  const [category, setCategory] = useState('Nutrition');
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [isPostingArticle, setIsPostingArticle] = useState(false); // Manage visibility of the article form

  // Handle the submission of a new article
  const handleSubmit = (e) => {
    e.preventDefault();
    const newArticle = {
      title,
      body,
      category,
      date: new Date().toLocaleDateString(),
      isExpanded: false,
    };
    setArticles([...articles, newArticle]);
    setTitle('');
    setBody('');
    setCategory('Nutrition');
    setIsSubmitted(true);
  };

  // Handle expanding an article to show full content
  const toggleExpand = (index) => {
    const updatedArticles = [...articles];
    updatedArticles[index].isExpanded = !updatedArticles[index].isExpanded;
    setArticles(updatedArticles);
  };

  return (
    <div className="article-container">
      {/* Button to toggle the article form */}
      <button className="post-article-btn" onClick={() => setIsPostingArticle(!isPostingArticle)}>
        Post Your Own Article
      </button>

      {/* Conditionally render the article form */}
      {isPostingArticle && (
        <form className="article-form" onSubmit={handleSubmit}>
          <h3>Publish a New Article</h3>
          <input
            type="text"
            placeholder="Article Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
          <textarea
            placeholder="Write your article body..."
            value={body}
            onChange={(e) => setBody(e.target.value)}
            required
          />
          <select value={category} onChange={(e) => setCategory(e.target.value)}>
            <option value="Nutrition">Nutrition</option>
            <option value="Workout">Workout</option>
            <option value="Health">Health</option>
            <option value="Fitness">Fitness</option>
          </select>
          <button type="submit">Publish Article</button>
          {isSubmitted && <p className="success-msg">Article Published Successfully!</p>}
        </form>
      )}

      <h1>Articles on Health, Fitness, and Nutrition</h1>
      
      {/* Display articles shared by others */}
      <div className="articles-list">
        {articles.map((article, index) => (
          <div className="article-item" key={index}>
            <h3>{article.title}</h3>
            <p className="article-date">{article.date}</p>
            <p className="article-category">{article.category}</p>
            <div className="article-body">
              {article.isExpanded ? (
                <p>{article.body}</p>
              ) : (
                <p>{article.body.slice(0, 100)}... <span onClick={() => toggleExpand(index)} className="read-more">Read More</span></p>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Article;
