// src/components/Navbar.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Register from './Register';
import Login from './Login'; // Import Login component
import './Navbar.css'; // Import your CSS file for styling

const Navbar = () => {
  const [showRegister, setShowRegister] = useState(false); // State to show/hide registration form
  const [showLogin, setShowLogin] = useState(false); // State to show/hide login form

  const handleSignUpClick = () => {
    setShowRegister(true); // Show registration form when Sign Up is clicked
  };

  const handleCloseRegister = () => {
    setShowRegister(false); // Close registration form when Close button is clicked
  };

  const handleLoginClick = () => {
    setShowLogin(true); // Show login form when Login is clicked
  };

  const handleCloseLogin = () => {
    setShowLogin(false); // Close login form when Close button is clicked
  };

  return (
    <div>
      <nav className="navbar">
        <div className="navbar-links">
        <Link to="/">Health&Me</Link>
          <Link to="/about-us">About Us</Link>
          <Link to="/category">Category</Link>
          <Link to="/articles">Articles</Link>
          <Link to="/stories">Stories</Link>
          <Link to="/exercise-tracking">Exercise Tracking</Link>
        </div>
        <div className="navbar-actions">
          <button className="login-button" onClick={handleLoginClick}>
            Login
          </button>
          <button className="signup-button" onClick={handleSignUpClick}>
            Sign Up
          </button>
          <Link to="/profile">
            <img src="/path/to/profile-icon.png" alt="Profile" className="profile-icon" />
          </Link>
        </div>
      </nav>

      {/* Display Register Form when the state is true */}
      {showRegister && <Register onClose={handleCloseRegister} />}
      
      {/* Display Login Form when the state is true */}
      {showLogin && <Login onClose={handleCloseLogin} />}
    </div>
  );
};

export default Navbar;
