import React, { useState } from 'react';
import './MyMedicalHistory.css';

const MyMedicalHistory = () => {
  const [formData, setFormData] = useState({
    weight: '',
    age: '',
    bloodGroup: '',
    healthIssues: '',
    description: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleDiscard = () => {
    setFormData({
      weight: '',
      age: '',
      bloodGroup: '',
      healthIssues: '',
      description: ''
    });
  };

  const handleSave = () => {
    console.log(formData);
    // Add save logic here
  };

  return (
    <div className="medical-history-edit">
      <h2>My Medical History <span role="img" aria-label="edit">✏️</span></h2>
      
      <div className="edit-field">
        <label>Weight:</label>
        <input
          type="number"
          name="weight"
          value={formData.weight}
          onChange={handleChange}
          placeholder="kgs"
        />
      </div>

      <div className="edit-field">
        <label>Age:</label>
        <input
          type="number"
          name="age"
          value={formData.age}
          onChange={handleChange}
          placeholder="yrs"
        />
      </div>

      <div className="edit-field">
        <label>Blood Group:</label>
        <input
          type="text"
          name="bloodGroup"
          value={formData.bloodGroup}
          onChange={handleChange}
        />
      </div>

      <div className="edit-field">
        <label>Health Issues or Diseases (if any):</label>
        <input
          type="text"
          name="healthIssues"
          value={formData.healthIssues}
          onChange={handleChange}
        />
      </div>

      <div className="edit-field">
        <label>Describe in Detail:</label>
        <textarea
          name="description"
          value={formData.description}
          onChange={handleChange}
        />
      </div>

      <div className="edit-actions">
        <button className="discard-button" onClick={handleDiscard}>Discard</button>
        <button className="save-button" onClick={handleSave}>Save Changes</button>
      </div>
    </div>
  );
};

export default MyMedicalHistory;
