import React, { useState, useEffect } from 'react';
import './ExerciseTracker.css';

const ExerciseTracker = () => {
    // Example data collected from a mobile device
    const [exerciseData, setExerciseData] = useState({
        steps: 12000,
        workoutHours: 1.5,
        caloriesBurned: 500,
        distance: 8.2, // Distance in kilometers
        activeMinutes: 75,
        waterIntake: 2.5, // Liters
    });

    // Optionally, you could fetch this data from an API or mobile data using useEffect
    useEffect(() => {
        // Simulating data update every 5 seconds
        const interval = setInterval(() => {
            setExerciseData((prevData) => ({
                ...prevData,
                steps: prevData.steps + Math.floor(Math.random() * 200),
                caloriesBurned: prevData.caloriesBurned + Math.floor(Math.random() * 10),
            }));
        }, 5000);

        return () => clearInterval(interval);
    }, []);

    return (
        <div className="dashboard-container">
            <h1>Exercise Tracker Dashboard</h1>
            <div className="dashboard-grid">
                <div className="dashboard-card">
                    <h3>Steps</h3>
                    <p>{exerciseData.steps.toLocaleString()} steps</p>
                </div>
                <div className="dashboard-card">
                    <h3>Workout Hours</h3>
                    <p>{exerciseData.workoutHours} hours</p>
                </div>
                <div className="dashboard-card">
                    <h3>Calories Burned</h3>
                    <p>{exerciseData.caloriesBurned} kcal</p>
                </div>
                <div className="dashboard-card">
                    <h3>Distance</h3>
                    <p>{exerciseData.distance} km</p>
                </div>
                <div className="dashboard-card">
                    <h3>Active Minutes</h3>
                    <p>{exerciseData.activeMinutes} minutes</p>
                </div>
                <div className="dashboard-card">
                    <h3>Water Intake</h3>
                    <p>{exerciseData.waterIntake} liters</p>
                </div>
            </div>
        </div>
    );
};

export default ExerciseTracker;
