import React from 'react';
import './Recipes.css';

const Recipes = () => {
  const recipes = [
    { id: 1, title: 'Vegetable Khichdi', image: '/images/khichdi.jpg', description: 'A wholesome, nutrient-packed dish made with rice and lentils.' },
    { id: 2, title: 'Paneer Tikka', image: '/images/paneer-tikka.jpg', description: 'Grilled paneer cubes marinated with spices and yogurt.' },
    { id: 3, title: 'Chana Masala', image: '/images/chana-masala.jpg', description: 'A protein-rich chickpea curry with Indian spices.' },
    { id: 4, title: 'Quinoa Pulao', image: '/images/quinoa-pulao.jpg', description: 'A healthy alternative to rice, made with quinoa and vegetables.' },
    { id: 5, title: 'Methi Thepla', image: '/images/methi-thepla.jpg', description: 'A Gujarati flatbread made with fenugreek leaves and spices.' },
    { id: 6, title: 'Oats Idli', image: '/images/oats-idli.jpg', description: 'Healthy, steamed South Indian idlis made with oats.' },
    { id: 7, title: 'Moong Dal Chilla', image: '/images/moong-dal-chilla.jpg', description: 'A savory pancake made with moong dal and spices.' },
    { id: 8, title: 'Palak Soup', image: '/images/palak-soup.jpg', description: 'A healthy spinach soup loaded with iron and fiber.' }
  ];

  return (
    <div className="recipes-container">
      <h1>Popular Indian Healthy Recipes</h1>
      <div className="carousel">
        <div className="carousel-wrapper">
          {recipes.map((recipe) => (
            <div key={recipe.id} className="carousel-item">
              <img src={recipe.image} alt={recipe.title} className="recipe-image" />
              <div className="recipe-details">
                <h3>{recipe.title}</h3>
                <p>{recipe.description}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Recipes;
