import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Navbar from './components/Navbar';
import Home from './components/Home';
import Profile from './components/Profile';
import Register from './components/Register';
import Login from './components/Login';
import Stories from './components/Stories';
import Footer from './components/Footer';
import Dashboard from "./components/Dashboard";
import DataCollection from "./components/DataCollection";
import ArI from "./components/ArI";
import AlertsReports from './components/AlertsReports';
import User from './components/User';
import SystemSettings from './components/SystemSettings';

function App() {
  const [showRegister, setShowRegister] = useState(false);

  // Simulated user role (Replace with actual authentication logic)
  const currentUserRole = "Admin"; // Change this to "Super Admin", "Editor", "Viewer" for testing

  const toggleRegister = () => {
    setShowRegister(!showRegister);
  };

  return (
    <Router>
      {/* Navbar is ALWAYS present */}
      <Navbar onSignUpClick={toggleRegister} /> 

      {/* Show Register Modal */}
      {showRegister && <Register onClose={toggleRegister} />}

      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/profile" element={<Profile />} />
        <Route path="/register" element={<Register />} />
        <Route path="/login" element={<Login />} />
        <Route path="/ai-analysis" element={<ArI />} />
        <Route path="/stories" element={<Stories />} />
        <Route path="/dashboard" element={<Dashboard />} />
        <Route path="/data-collection" element={<DataCollection />} />
        <Route path="/alerts-reports" element={<AlertsReports />} />
        <Route path="/settings" element={<SystemSettings />} /> {/* Fixes Settings Navigation */}
        <Route path="/user" element={<User currentUserRole={currentUserRole} />} />
      </Routes>

      <Footer />
    </Router>
  );
}

export default App;
