import React, { useState, useEffect } from "react";
import "./AlertsReports.css";

const AlertsReports = () => {
  const [filter, setFilter] = useState("All");
  const [selectedCountry, setSelectedCountry] = useState("");
  const [selectedDateFilter, setSelectedDateFilter] = useState("All Dates");
  const [specificDate, setSpecificDate] = useState("");
  const [alerts, setAlerts] = useState([]);
  const [loading, setLoading] = useState(false);

  const API_KEY = "pub_788677aa36735d8282ee66b9146090ddf3d0f"; // Replace with your actual API key

  useEffect(() => {
    const fetchNewsAlerts = async () => {
      setLoading(true);
      try {
        const response = await fetch(
          `https://newsdata.io/api/1/news?apikey=${API_KEY}&country=${selectedCountry}&category=politics,disaster&language=en`
        );
        const data = await response.json();
        const newsAlerts = data.results.map((news, index) => ({
          id: index + 1,
          date: news.pubDate.split(" ")[0],
          country: news.country[0],
          title: news.title,
          description: news.description,
          link: news.link,
        }));
        setAlerts(newsAlerts);
      } catch (error) {
        console.error("Error fetching news alerts:", error);
      }
      setLoading(false);
    };

    fetchNewsAlerts();
  }, [selectedCountry]);

  const applyDateFilter = (alert) => {
    const alertDate = new Date(alert.date);
    const today = new Date();

    if (selectedDateFilter === "Last Week") {
      const lastWeek = new Date();
      lastWeek.setDate(today.getDate() - 7);
      return alertDate >= lastWeek;
    }

    if (selectedDateFilter === "Last Month") {
      const lastMonth = new Date();
      lastMonth.setDate(today.getDate() - 30);
      return alertDate >= lastMonth;
    }

    if (selectedDateFilter === "Specific Date" && specificDate) {
      return alert.date === specificDate;
    }

    return true; // If "All Dates" is selected, return all alerts
  };

  const filteredAlerts = alerts.filter(applyDateFilter);

  return (
    <div className="alerts-reports-container">
      <h1 className="title">⚠️ Global Geopolitical Alerts & Reports</h1>

      {/* Filter Controls */}
      <div className="controls">
        <select
          className="filter-dropdown"
          value={selectedCountry}
          onChange={(e) => setSelectedCountry(e.target.value)}
        >
          <option value="">Select Country</option>
          <option value="us">USA</option>
          <option value="ca">Canada</option>
          <option value="gb">UK</option>
          <option value="de">Germany</option>
          <option value="in">India</option>
          {/* Add more country options as needed */}
        </select>

        {/* Date Filter */}
        <select
          className="filter-dropdown"
          value={selectedDateFilter}
          onChange={(e) => setSelectedDateFilter(e.target.value)}
        >
          <option value="All Dates">All Dates</option>
          <option value="Last Week">Last Week</option>
          <option value="Last Month">Last Month</option>
          <option value="Specific Date">Specific Date</option>
        </select>

        {selectedDateFilter === "Specific Date" && (
          <input
            type="date"
            className="date-picker"
            value={specificDate}
            onChange={(e) => setSpecificDate(e.target.value)}
          />
        )}
      </div>

      {/* Alerts Table */}
      {loading ? (
        <p>Loading alerts...</p>
      ) : (
        <table className="alerts-table">
          <thead>
            <tr>
              <th>Date</th>
              <th>Country</th>
              <th>Title</th>
              <th>Description</th>
              <th>Link</th>
            </tr>
          </thead>
          <tbody>
            {filteredAlerts.length > 0 ? (
              filteredAlerts.map((alert) => (
                <tr key={alert.id}>
                  <td>{alert.date}</td>
                  <td>{alert.country}</td>
                  <td>{alert.title}</td>
                  <td>{alert.description}</td>
                  <td>
                    <a href={alert.link} target="_blank" rel="noopener noreferrer">
                      Read more
                    </a>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5" className="no-alerts">
                  No alerts found.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default AlertsReports;
