// CategoryCarousel.js
import React, { useState } from 'react';
import Modal from './Modal'; // Import the Modal component

const CategoryCarousel = ({ title, category }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedProgram, setSelectedProgram] = useState(null);

  const handleProgramClick = (program) => {
    setSelectedProgram(program);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedProgram(null);
  };

  return (
    <div>
      <h2>{title}</h2>
      <div className="carousel">
        {category.map((program, index) => (
          <div key={index} className="carousel-item" onClick={() => handleProgramClick(program)}>
            <img src={program.image} alt={program.title} />
            <h3>{program.title}</h3>
            <p>{program.description.substring(0, 60)}...</p>
          </div>
        ))}
      </div>
      {selectedProgram && (
        <Modal
          isOpen={isModalOpen}
          onClose={closeModal}
          title={selectedProgram.title}
          description={selectedProgram.description}
          detailedDescription={selectedProgram.detailedDescription} // Pass detailed description
          image={selectedProgram.image}
        />
      )}
    </div>
  );
};

export default CategoryCarousel;
