import React, { useState } from 'react';
// ... your other imports

const AppContent = () => {
  const location = useLocation();
  const isLoginPage = location.pathname === '/';

  // ðŸ’¡ Example: Managing role in state (can be set from LoginPage too)
  const [userRole, setUserRole] = useState("Admin"); // Default is Admin for now

  return (
    <div className="app-container">
      {!isLoginPage && <Navbar />}

      <main className="main-content">
        <Routes>
          <Route path="/" element={<LoginPage setUserRole={setUserRole} />} />
          <Route path="/home" element={<PrivateRoute element={<Home />} />} />
          <Route path="/profile" element={<PrivateRoute element={<Profile />} />} />
          <Route path="/ai-analysis" element={<PrivateRoute element={<ArI />} />} />
          <Route path="/dashboard" element={<PrivateRoute element={<Dashboard />} />} />
          <Route path="/data-collection" element={<PrivateRoute element={<DataCollection />} />} />
          <Route path="/AlertsReports" element={<PrivateRoute element={<AlertsReports />} />} />
          <Route path="/settings" element={<PrivateRoute element={<SystemSettings />} />} />

          {/* âœ… Now user role is passed dynamically */}
          <Route path="/user" element={<PrivateRoute element={<User currentUserRole={userRole} />} />} />
        </Routes>
      </main>

      {!isLoginPage && <Footer />}
    </div>
  );
};
