import React, { useState, useEffect } from "react";
import "./Dashboard.css"; // Importing CSS

const Dashboard = () => {
  const [countries, setCountries] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState("");
  const [activePanel, setActivePanel] = useState("Live News Feed");

  // Fetch country list
  useEffect(() => {
    fetch("https://restcountries.com/v3.1/all")
      .then((response) => response.json())
      .then((data) => {
        const countryNames = data.map((country) => country.name.common).sort();
        setCountries(countryNames);
      })
      .catch((error) => console.error("Error fetching countries:", error));
  }, []);

  return (
    <div className="dashboard">
      <h1 className="title">Dashboard</h1>

      {/* Country Dropdown */}
      <div className="dropdown-container">
        <label>Select a Country:</label>
        <select
          className="dropdown"
          value={selectedCountry}
          onChange={(e) => setSelectedCountry(e.target.value)}
        >
          <option value="">-- Choose a Country --</option>
          {countries.map((country, index) => (
            <option key={index} value={country}>
              {country}
            </option>
          ))}
        </select>
      </div>

      {/* Buttons */}
      <div className="buttons">
        <button
          className={activePanel === "Live News Feed" ? "active" : ""}
          onClick={() => setActivePanel("Live News Feed")}
        >
          Live News Feed
        </button>
        <button
          className={activePanel === "AI-Analysis Summary" ? "active" : ""}
          onClick={() => setActivePanel("AI-Analysis Summary")}
        >
          AI-Analysis Summary
        </button>
        <button
          className={activePanel === "Recent Alerts Panel" ? "active" : ""}
          onClick={() => setActivePanel("Recent Alerts Panel")}
        >
          Recent Alerts Panel
        </button>
      </div>

      {/* Dynamic Content */}
      <div className="content">
        {activePanel === "Live News Feed" && (
          <div className="panel">üì¢ Showing **Live News Feed** for {selectedCountry || "your selection"}.</div>
        )}
        {activePanel === "AI-Analysis Summary" && (
          <div className="panel">ü§ñ AI-generated **Analysis Summary** for {selectedCountry || "your selection"}.</div>
        )}
        {activePanel === "Recent Alerts Panel" && (
          <div className="panel">‚ö†Ô∏è **Recent Alerts** for {selectedCountry || "your selection"}.</div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
