import React, { useState } from 'react';
import './Login.css'; // Importing the CSS for styling

const Login = ({ onClose }) => {
  const [credentials, setCredentials] = useState({
    email: '',
    password: ''
  });
  const [isLoggedIn, setIsLoggedIn] = useState(false); // To track if login is successful
  const [isFormVisible, setIsFormVisible] = useState(true); // Controls the visibility of the form

  const handleChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Simulate a login action (replace with actual logic)
    setIsLoggedIn(true);
    setTimeout(() => {
      onClose(); // Close the login form after a successful login
    }, 2000); // Wait for 2 seconds before closing
  };

  const handleClose = () => {
    setIsFormVisible(false); // Hide the form when Close button is clicked
  };

  return (
    <div className={`login-overlay ${isFormVisible ? '' : 'hidden'}`}>
      <div className="login-form">
        {isLoggedIn ? (
          <div className="login-success">
            <h2>Logged in successfully!</h2>
          </div>
        ) : (
          <>
            <h2>Login</h2>
            <form onSubmit={handleSubmit}>
              <div className="input-container">
                <input
                  type="email"
                  name="email"
                  placeholder="Email"
                  value={credentials.email}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="input-container">
                <input
                  type="password"
                  name="password"
                  placeholder="Password"
                  value={credentials.password}
                  onChange={handleChange}
                  required
                />
              </div>
              <button type="submit">Login</button>
            </form>
            <button type="button" onClick={handleClose} className="close-btn">
              Close
            </button>
          </>
        )}
      </div>
    </div>
  );
};

export default Login;
