import React, { useState } from "react";
import "./DataCollection.css";

const DataCollection = () => {
  const [sources, setSources] = useState([""]);
  const [summary, setSummary] = useState("");
  const [loading, setLoading] = useState(false);

  const handleInputChange = (index, value) => {
    const updated = [...sources];
    updated[index] = value;
    setSources(updated);
  };

  const addSource = () => {
    setSources([...sources, ""]);
  };

  const handleSelect = async (index) => {
    const url = sources[index];
    if (!url.startsWith("https://")) {
      alert("Please enter a valid URL!");
      return;
    }

    try {
      setLoading(true);
      const res = await fetch("http://localhost:5000/analyze", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ url })
      });
      const data = await res.json();
      setSummary(data.summary);
    } catch (err) {
      console.error("Error:", err);
      setSummary("An error occurred while analyzing the URL.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="data-collection">
      <h2 className="title">Early Warning News Analyzer</h2>
      <div className="sources-container">
        {sources.map((source, index) => (
          <div key={index} className="source-input">
            <input
              type="text"
              value={source}
              onChange={(e) => handleInputChange(index, e.target.value)}
              placeholder={`Enter News URL ${index + 1}`}
            />
            <button className="select-button" onClick={() => handleSelect(index)}>
              Analyze
            </button>
          </div>
        ))}
      </div>
      <div className="add-source-container">
        <button className="add-source" onClick={addSource}>
          + Add New Source
        </button>
      </div>
      {loading ? (
        <p className="loading">Analyzing, please wait...</p>
      ) : (
        summary && (
          <div className="summary-box">
            <h3>üîç Summary:</h3>
            <pre>{summary}</pre>
          </div>
        )
      )}
    </div>
  );
};

export default DataCollection;
