import React, { useState, useEffect } from "react";
import "./Dashboard.css";

const Dashboard = () => {
  const [countries, setCountries] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState("");
  const [activePanel, setActivePanel] = useState("news");

  // Fetch countries from REST API
  useEffect(() => {
    fetch("https://restcountries.com/v3.1/all")
      .then((response) => response.json())
      .then((data) => {
        const countryNames = data.map((country) => country.name.common).sort();
        setCountries(countryNames);
      })
      .catch((error) => console.error("Error fetching countries:", error));
  }, []);

  return (
    <div className="dashboard">
      {/* Title */}
      <h1 className="title">Dashboard</h1>

      {/* Country Dropdown */}
      <div className="dropdown-container">
        <label>Select a Country:</label>
        <select
          className="dropdown"
          value={selectedCountry}
          onChange={(e) => setSelectedCountry(e.target.value)}
        >
          <option value="">-- Choose a Country --</option>
          {countries.map((country, index) => (
            <option key={index} value={country}>
              {country}
            </option>
          ))}
        </select>
      </div>

      {/* Live News Feed Text */}
      <div className="live-news">
        ðŸ“¢ Showing <strong>Live News Feed</strong> for your selection
      </div>

      {/* Buttons - Positioned Directly Below Live News Feed */}
      <div className="buttons">
        <button
          className={activePanel === "news" ? "active" : ""}
          onClick={() => setActivePanel("news")}
        >
          Live News Feed
        </button>
        <button
          className={activePanel === "ai" ? "active" : ""}
          onClick={() => setActivePanel("ai")}
        >
          AI-Analysis Summary
        </button>
        <button
          className={activePanel === "alerts" ? "active" : ""}
          onClick={() => setActivePanel("alerts")}
        >
          Recent Alerts Panel
        </button>
      </div>

      {/* Content Panel */}
      <div className="content">
        {activePanel === "news" && <div className="panel">Live News Feed Content</div>}
        {activePanel === "ai" && <div className="panel">AI-Analysis Summary Content</div>}
        {activePanel === "alerts" && <div className="panel">Recent Alerts Panel Content</div>}
      </div>
    </div>
  );
};

export default Dashboard;
