import React, { useState } from 'react';
import './Stories.css';

const Stories = () => {
    // Sample stories data with Indian names and 'kgs' term
    const [stories, setStories] = useState([
        {
            id: 1,
            name: "Rohit Sharma",
            date: "October 12, 2024",
            content: "I lost 12 kgs in 4 months by following a structured workout and diet plan through Health&me. Feeling more energetic!",
            comments: [],
        },
        {
            id: 2,
            name: "Pooja Patel",
            date: "October 10, 2024",
            content: "Health&me guided me to lose 8 kgs in 3 months. I also improved my mental health with meditation and regular yoga.",
            comments: [],
        },
        {
            id: 3,
            name: "Amit Desai",
            date: "October 8, 2024",
            content: "I gained 5 kgs of muscle mass in just 2 months by following the personalized training and nutrition plan. Super happy with the results!",
            comments: [],
        },
        {
            id: 4,
            name: "Sunita Reddy",
            date: "October 6, 2024",
            content: "I managed to shed 10 kgs in 6 months and completely transformed my lifestyle. Health&me has been a life-changer for me!",
            comments: [],
        },
        {
            id: 5,
            name: "Sandeep Gupta",
            date: "October 4, 2024",
            content: "Through Health&me, I lost 7 kgs in 3 months and have never felt fitter. Their meal plans and workouts are incredibly effective!",
            comments: [],
        },
        {
            id: 6,
            name: "Meera Iyer",
            date: "October 2, 2024",
            content: "Thanks to Health&me, I was able to lose 6 kgs in 2 months while managing my diet and doing daily cardio workouts.",
            comments: [],
        },
    ]);

    // State to handle new comments
    const [newComment, setNewComment] = useState("");

    // Handle adding a comment
    const handleAddComment = (storyId) => {
        const updatedStories = stories.map(story => {
            if (story.id === storyId && newComment.trim() !== "") {
                return {
                    ...story,
                    comments: [...story.comments, newComment]
                };
            }
            return story;
        });
        setStories(updatedStories);
        setNewComment(""); // Reset comment field
    };

    return (
        <div className="stories-container">
            <h1>Fitness Progress Stories</h1>
            <div className="stories-tiles">
                {stories.map(story => (
                    <div key={story.id} className="story-tile">
                        <h3>{story.name}</h3>
                        <p className="story-date">{story.date}</p>
                        <p>{story.content}</p>

                        <div className="comments-section">
                            <h4>Comments:</h4>
                            {story.comments.length > 0 ? (
                                <ul className="comments-list">
                                    {story.comments.map((comment, index) => (
                                        <li key={index}>{comment}</li>
                                    ))}
                                </ul>
                            ) : (
                                <p>No comments yet.</p>
                            )}

                            <div className="comment-input-container">
                                <input
                                    type="text"
                                    placeholder="Write a comment..."
                                    value={newComment}
                                    onChange={(e) => setNewComment(e.target.value)}
                                    className="comment-input"
                                />
                                <button
                                    onClick={() => handleAddComment(story.id)}
                                    className="comment-btn"
                                >
                                    Add Comment
                                </button>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Stories;
