import React, { useState } from "react";
import "./AlertsReports.css";
import { FaDownload, FaSearch } from "react-icons/fa";

const AlertsReports = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [filter, setFilter] = useState("All");

  const alerts = [
    { id: 1, type: "Critical", message: "Cyber attack detected", date: "2025-03-29" },
    { id: 2, type: "Warning", message: "Unusual login attempt", date: "2025-03-28" },
    { id: 3, type: "Info", message: "System update completed", date: "2025-03-27" },
  ];

  const filteredAlerts = alerts.filter(alert =>
    (filter === "All" || alert.type === filter) &&
    alert.message.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="alerts-reports-container">
      <h1 className="title">Alerts & Reports</h1>
      
      <div className="controls">
        <input
          type="text"
          placeholder="Search alerts..."
          className="search-bar"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <FaSearch className="search-icon" />

        <select
          className="filter-dropdown"
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
        >
          <option value="All">All</option>
          <option value="Critical">Critical</option>
          <option value="Warning">Warning</option>
          <option value="Info">Info</option>
        </select>
      </div>

      <table className="alerts-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Type</th>
            <th>Message</th>
            <th>Date</th>
          </tr>
        </thead>
        <tbody>
          {filteredAlerts.map((alert) => (
            <tr key={alert.id} className={alert.type.toLowerCase()}>
              <td>{alert.id}</td>
              <td>{alert.type}</td>
              <td>{alert.message}</td>
              <td>{alert.date}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <button className="download-btn">
        <FaDownload /> Download Reports
      </button>
    </div>
  );
};

export default AlertsReports;
