import React, { useState } from 'react';
import './MyArticles.css';

const MyArticles = () => {
  const [articles, setArticles] = useState([
    {
      id: 1,
      title: 'Healthy Eating Habits',
      content: 'This article discusses the importance of healthy eating habits. A well-balanced diet...',
      datePosted: '2024-10-01',
      comments: 2,
      commentDetails: [
        "Great article, very informative!",
        "This helped me understand my diet better."
      ],
      expanded: false,
      viewComments: false
    },
    {
      id: 2,
      title: 'Benefits of Regular Exercise',
      content: 'Exercising regularly has a number of benefits for your body...',
      datePosted: '2024-10-05',
      comments: 3,
      commentDetails: [
        "I need to start working out more!",
        "The mental health benefits are real.",
        "Thanks for motivating me!"
      ],
      expanded: false,
      viewComments: false,
    },
    {
      id: 3,
      title: 'Stress Management Techniques',
      content: 'Managing stress effectively can improve mental and physical health...',
      datePosted: '2024-10-12',
      comments: 4,
      commentDetails: [
        "Mindfulness is a game-changer!",
        "I appreciate the practical advice.",
        "I have been using these techniques for months.",
        "Do you have any recommendations for apps?"
      ],
      expanded: false,
      viewComments: false
    }
  ]);

  // Function to handle expanding the article content
  const handleExpand = (id) => {
    setArticles(articles.map(article => 
      article.id === id ? { ...article, expanded: !article.expanded } : article
    ));
  };

  // Function to toggle viewing comments
  const handleViewComments = (id) => {
    setArticles(articles.map(article =>
      article.id === id ? { ...article, viewComments: !article.viewComments } : article
    ));
  };

  return (
    <div className="articles-container">
      <h2>My Articles</h2>
      <div className="articles-row">
        {articles.map((article) => (
          <div key={article.id} className="article-item">
            <div className="article-thumbnail" onClick={() => handleExpand(article.id)}>
              <h3>{article.title}</h3>
              <p className="article-date">Posted on: {article.datePosted}</p>
              <button className="expand-btn">{article.expanded ? 'Collapse' : 'Read'}</button>
            </div>

            {article.expanded && (
              <div className="article-content">
                <p>{article.content}</p>
              </div>
            )}
            <p className="article-comments">
                Comments: {article.comments} 
                <button className="view-comments-btn" onClick={(e) => { e.stopPropagation(); handleViewComments(article.id); }}>
                  {article.viewComments ? 'Hide Comments' : 'View'}
                </button>
              </p>
            {article.viewComments && (
              <div className="comments-section">
                <ul>
                  {article.commentDetails.map((comment, index) => (
                    <li key={index}>{comment}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default MyArticles;
