import React, { useState } from 'react';
import './MyProgress.css';

const MyProgress = () => {
  const [name, setName] = useState('');
  const [story, setStory] = useState('');
  const [beforeImage, setBeforeImage] = useState(null);
  const [afterImage, setAfterImage] = useState(null);

  const handleImageUpload = (event, type) => {
    const file = event.target.files[0];
    if (type === 'before') {
      setBeforeImage(file);
    } else {
      setAfterImage(file);
    }
  };

  const handleSubmit = () => {
    // Post the progress story and images to the "Stories" section
    // You can send this data to your backend for further processing
    const progressData = {
      name,
      story,
      beforeImage,
      afterImage,
    };

    console.log('Progress submitted:', progressData);
    alert('Your progress story has been posted to the Stories section!');
    // Clear form fields after submission
    resetFields();
  };

  const resetFields = () => {
    setName('');
    setStory('');
    setBeforeImage(null);
    setAfterImage(null);
  };

  return (
    <div className="progress-container">
      <h2>My Progress</h2>
      <form className="progress-form">
        <label htmlFor="name">Name</label>
        <input
          type="text"
          id="name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Enter your name"
        />

        <label htmlFor="story">Your Progress Story</label>
        <textarea
          id="story"
          value={story}
          onChange={(e) => setStory(e.target.value)}
          placeholder="Describe your progress"
        />

        <div className="image-upload">
          <label htmlFor="beforeImage">Before Image</label>
          <input
            type="file"
            id="beforeImage"
            onChange={(e) => handleImageUpload(e, 'before')}
          />
          {beforeImage && <p>{beforeImage.name}</p>}
        </div>

        <div className="image-upload">
          <label htmlFor="afterImage">After Image</label>
          <input
            type="file"
            id="afterImage"
            onChange={(e) => handleImageUpload(e, 'after')}
          />
          {afterImage && <p>{afterImage.name}</p>}
        </div>

        <div className="buttons">
          <button type="button" className="submit-btn" onClick={handleSubmit}>
            Submit
          </button>
          <button type="button" className="discard-btn" onClick={resetFields}>
            Discard
          </button>
        </div>
      </form>
    </div>
  );
};

export default MyProgress;
