import React, { useEffect, useRef } from "react";
import Chart from "chart.js/auto";
import "./AI.css"; // Import CSS for styling

const AIAnalysis = () => {
  const sentimentChartRef = useRef(null);
  const sentimentChartInstance = useRef(null);

  useEffect(() => {
    if (sentimentChartInstance.current) {
      sentimentChartInstance.current.destroy();
    }

    const ctx = sentimentChartRef.current.getContext("2d");

    sentimentChartInstance.current = new Chart(ctx, {
      type: "bar",
      data: {
        labels: ["Positive", "Neutral", "Negative"],
        datasets: [
          {
            label: "Sentiment Score",
            data: [45, 30, 25], // Sample data
            backgroundColor: ["green", "gray", "red"],
            borderWidth: 1,
          },
        ],
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        plugins: {
          legend: { display: false },
        },
        scales: {
          y: { beginAtZero: true, max: 50 },
        },
      },
    });

    return () => {
      if (sentimentChartInstance.current) {
        sentimentChartInstance.current.destroy();
      }
    };
  }, []);

  return (
    <div className="report-container">
      <h1 className="report-title">AI News Analysis Report</h1>

      <div className="report-section">
        <h2>üìå News Summary</h2>
        <p><strong>Title:</strong> Maharashtra Protest: Major Demonstrations Across the State</p>
        <p><strong>Authenticity Score:</strong> 87%</p>
        <p><strong>Sentiment:</strong> Mixed (45% Positive, 30% Neutral, 25% Negative)</p>
        <p><strong>Summary:</strong> The state of Maharashtra witnessed widespread protests regarding various political and economic issues. Authorities have responded with heavy security measures, and public opinion remains divided.</p>
        <p><strong>Source:</strong> <a href="https://www.ndtv.com/topic/maharashtra-protest" target="_blank" rel="noopener noreferrer">NDTV - Maharashtra Protest</a></p>
      </div>

      <div className="small-chart-container">
        <h2>üìä Sentiment Analysis</h2>
        <canvas id="sentimentChart" ref={sentimentChartRef}></canvas>
      </div>

      <div className="report-section">
        <h2>üìà Trend Analysis</h2>
        <p>Recent trends show an increase in protest-related discussions across social media and news platforms. Government and opposition parties have responded with contrasting statements.</p>
      </div>

      <div className="report-section">
        <h2>üîç Fact Check</h2>
        <p>üîπ Verified reports confirm the legitimacy of the protests.<br />
           üîπ Some misinformation regarding protest numbers was detected on social media.<br />
           üîπ Official sources suggest peaceful negotiations are underway.</p>
      </div>
    </div>
  );
};

export default AIAnalysis;
