// src/components/Login.js
import React, { useState } from 'react';
import './Login.css'; // Importing the CSS for styling

const Login = ({ onClose }) => {
  const [credentials, setCredentials] = useState({
    email: '',
    password: ''
  });
  const [isLoggedIn, setIsLoggedIn] = useState(false); // To track if login is successful

  const handleChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Simulate a login action (replace with actual logic)
    setIsLoggedIn(true);
    setTimeout(() => {
      onClose(); // Close the login form after a successful login
    }, 2000); // Wait for 2 seconds before closing
  };

  const handleClose = () => {
    onClose(); // Close the login form immediately when Close button is clicked
  };

  return (
    <div className="login-overlay">
      <div className="login-form">
        {isLoggedIn ? (
          <div className="login-success">
            <h2>Logged in successfully!</h2>
          </div>
        ) : (
          <>
            <h2>Login</h2>
            <form onSubmit={handleSubmit}>
              <div className="input-container">
                <input
                  type="email"
                  name="email"
                  placeholder="Email"
                  value={credentials.email}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="input-container">
                <input
                  type="password"
                  name="password"
                  placeholder="Password"
                  value={credentials.password}
                  onChange={handleChange}
                  required
                />
              </div>
              <button type="submit">Login</button>
              <button type="button" className="close-btn" onClick={handleClose}>
                X
              </button>
            </form>
          </>
        )}
      </div>
    </div>
  );
};

export default Login;
