import React from 'react';
import './Recipes.css';

const Recipies = () => {
  const recipes = [
    {
      id: 1,
      title: 'Paneer Tikka',
      description: 'A healthy vegetarian option rich in protein.',
      image: '/images/r1.jpeg',
    },
    {
      id: 2,
      title: 'Quinoa Salad',
      description: 'A low-calorie, high-protein salad with veggies.',
      image: '/images/r2.jpeg',
    },
    {
      id: 3,
      title: 'Methi Thepla',
      description: 'A nutritious Gujarati flatbread made with fenugreek leaves.',
      image: '/images/r3.jpeg',
    },
    {
      id: 4,
      title: 'Masala Oats',
      description: 'A healthy breakfast option with lots of veggies.',
      image: '/images/r4.jpeg',
    },
    {
      id: 5,
      title: 'Palak Soup',
      description: 'Spinach soup, a rich source of iron and fiber.',
      image: '/images/r5.jpeg',
    },
    {
      id: 6,
      title: 'Chia Pudding',
      description: 'A high-protein dessert made with chia seeds and fruits.',
      image: '/images/r6.jpeg',
    },
  ];

  return (
    <div className="recipes-container">
      <h1>Popular Indian Healthy Recipes</h1>
      <div className="recipes-carousel">
        {recipes.map((recipe) => (
          <div key={recipe.id} className="recipe-tile">
            <img
              src={recipe.image}
              alt={recipe.title}
              className="recipe-image"
              onError={(e) => e.target.src = 'https://via.placeholder.com/300'} // Fallback image if local image is missing
            />
            <h3>{recipe.title}</h3>
            <p>{recipe.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Recipes;
