import React, { useState, useEffect } from "react";
import "./Dashboard.css";

const Dashboard = () => {
  const [activePanel, setActivePanel] = useState("Live News Feed");
  const [news, setNews] = useState([]);
  const [aiSummary, setAiSummary] = useState([]);
  const [loading, setLoading] = useState(true);

  // Use 'IN' as fixed country code for India
  const countryCode = "in";

  useEffect(() => {
    const fetchIndianNews = async () => {
      try {
        const response = await fetch(`/api/news?country=${countryCode}`);
        const data = await response.json();
        setNews(data.results || []);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching Indian news:", error);
        setNews([]);
        setLoading(false);
      }
    };

    fetchIndianNews();
  }, []);

  useEffect(() => {
    const getAISummaryFromGemini = async () => {
      if (news.length === 0) return;

      const titles = news.slice(0, 5).map((n, i) => `${i + 1}. ${n.title}`).join("\n");

      try {
        const response = await fetch("/api/gemini-summary", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ prompt: `Summarize these headlines:\n${titles}` }),
        });

        const data = await response.json();
        setAiSummary(data.summary.split("\n").filter((line) => line.trim() !== ""));
      } catch (err) {
        console.error("Error getting AI summary:", err);
        setAiSummary(["âš ï¸ Gemini failed to summarize."]);
      }
    };

    getAISummaryFromGemini();
  }, [news]);

  const getAlerts = () => {
    const keywords = [
      "alert",
      "emergency",
      "warning",
      "disaster",
      "evacuate",
      "flood",
      "fire",
      "storm",
    ];
    return news.filter((n) =>
      keywords.some((kw) =>
        (n.title + " " + n.description).toLowerCase().includes(kw)
      )
    );
  };

  const recentAlerts = getAlerts();

  return (
    <div className="dashboard-container">
      <h1 className="dashboard-title">ðŸ‡®ðŸ‡³ India News Dashboard</h1>

      <div className="dashboard-panel-buttons">
        {["Live News Feed", "AI-Analysis Summary", "Recent Alerts Panel"].map(
          (panel) => (
            <button
              key={panel}
              className={`dashboard-button ${
                activePanel === panel ? "dashboard-active" : ""
              }`}
              onClick={() => setActivePanel(panel)}
            >
              {panel}
            </button>
          )
        )}
      </div>

      <div className="dashboard-panel-content">
        {activePanel === "Live News Feed" && (
          <div className="dashboard-news-feed">
            {loading ? (
              <p>Loading news...</p>
            ) : news.length > 0 ? (
              news.slice(0, 6).map((article, idx) => (
                <div className="dashboard-news-card" key={idx}>
                  <h3>{article.title}</h3>
                  <p>{article.description?.slice(0, 100)}...</p>
                  <a
                    href={article.link}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    Read More â†’
                  </a>
                </div>
              ))
            ) : (
              <p>No news found.</p>
            )}
          </div>
        )}

        {activePanel === "AI-Analysis Summary" && (
          <div className="dashboard-ai-panel">
            <h2>ðŸ§  AI-Based Summary</h2>
            <ul className="ai-summary-list">
              {aiSummary.length > 0 ? (
                aiSummary.map((summary, idx) => <li key={idx}>{summary}</li>)
              ) : (
                <li>Loading AI summary...</li>
              )}
            </ul>
          </div>
        )}

        {activePanel === "Recent Alerts Panel" && (
          <div className="dashboard-alert-panel">
            <h2>ðŸš¨ Recent Alerts</h2>
            {recentAlerts.length > 0 ? (
              recentAlerts.map((alert, idx) => (
                <div className="dashboard-alert-card" key={idx}>
                  <h4>{alert.title}</h4>
                  <p>{alert.description?.slice(0, 100)}...</p>
                  <a
                    href={alert.link}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    Read More â†’
                  </a>
                </div>
              ))
            ) : (
              <p>No major alerts found.</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
