import React, { useState } from 'react';
import './Articles.css';

const Article = () => {
  // Sample articles data (could be fetched from an API)
  const sampleArticles = [
    { title: "The Benefits of Regular Exercise", body: "Exercise helps to improve health by...", category: "Exercise", date: "2024-10-01" },
    { title: "Understanding Nutrition and Diet", body: "A balanced diet is important for...", category: "Nutrition", date: "2024-10-02" },
    { title: "Top Workout Routines for Beginners", body: "If you're new to working out...", category: "Workout", date: "2024-10-03" },
    // Add more sample articles here...
  ];

  const [articles, setArticles] = useState(sampleArticles);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [title, setTitle] = useState('');
  const [body, setBody] = useState('');
  const [category, setCategory] = useState('Nutrition');
  const [isSubmitted, setIsSubmitted] = useState(false);

  // Handle the submission of a new article
  const handleSubmit = (e) => {
    e.preventDefault();
    const newArticle = {
      title,
      body,
      category,
      date: new Date().toLocaleDateString(),
    };
    setArticles([newArticle, ...articles]);
    setTitle('');
    setBody('');
    setCategory('Nutrition');
    setIsSubmitted(true);
    setIsModalOpen(false); // Close the modal after submitting the article
  };

  return (
    <div className="article-container">
      <div className="navbar">
        <button className="post-article-button" onClick={() => setIsModalOpen(true)}>
          Post Your Own Article
        </button>
      </div>

      <div className="articles-tiles">
        {articles.slice(0, 10).map((article, index) => (
          <div className="article-tile" key={index}>
            <h3>{article.title}</h3>
            <p>{article.body.slice(0, 100)}...</p>
            <p className="article-category">{article.category}</p>
            <p className="article-date">{article.date}</p>
          </div>
        ))}
      </div>

      {/* Modal for posting an article */}
      {isModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={() => setIsModalOpen(false)}>&times;</span>
            <form onSubmit={handleSubmit} className="article-form">
              <h3>Publish a New Article</h3>
              <input
                type="text"
                placeholder="Article Title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                required
              />
              <textarea
                placeholder="Write your article body..."
                value={body}
                onChange={(e) => setBody(e.target.value)}
                required
              />
              <select value={category} onChange={(e) => setCategory(e.target.value)}>
                <option value="Nutrition">Nutrition</option>
                <option value="Workout">Workout</option>
                <option value="Health">Health</option>
                <option value="Fitness">Fitness</option>
              </select>
              <button type="submit">Publish Article</button>
              {isSubmitted && <p className="success-msg">Article Published Successfully!</p>}
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Article;
