import React, { useState, useEffect } from "react";
import "./Dashboard.css";

const Dashboard = () => {
  const [news, setNews] = useState([]);
  const [activePanel, setActivePanel] = useState("Live News Feed");
  const [aiSummary, setAiSummary] = useState([]);
  const [alerts, setAlerts] = useState([]);

  const NEWS_API_KEY = "pub_788677aa36735d8282ee66b9146090ddf3d0f"; // üîÅ Replace with your real NewsData.io API Key
  const GEMINI_API_KEY = "AIzaSyCgz-p2aVMhAfPapKiuqpPk5fSU1mczYgI"; // üîÅ Replace with your real Gemini API Key

  // üì∞ Fetch India news on mount
  useEffect(() => {
    const fetchNews = async () => {
      try {
        const res = await fetch(
          `https://newsdata.io/api/1/news?apikey=${NEWS_API_KEY}&country=in&language=en`
        );
        const data = await res.json();
        const articles = data.results || [];

        setNews(articles);

        // Detect alerts
        const alertKeywords = [
          "alert",
          "emergency",
          "warning",
          "disaster",
          "evacuate",
          "flood",
          "fire",
          "storm",
        ];
        const filtered = articles.filter((article) =>
          alertKeywords.some((keyword) =>
            (article.title + " " + article.description)
              .toLowerCase()
              .includes(keyword)
          )
        );
        setAlerts(filtered);

        // Send to Gemini for AI summary
        generateAISummary(articles.slice(0, 5)); // only send top 5
      } catch (error) {
        console.error("Failed to fetch news:", error);
      }
    };

    fetchNews();
  }, []);

  // üß† Generate AI Summary using Gemini
  const generateAISummary = async (articles) => {
    const prompt = `Summarize the following news headlines in bullet points:\n\n${articles
      .map((a, i) => `${i + 1}. ${a.title}`)
      .join("\n")}`;

    try {
      const res = await fetch(
        "https://generativelanguage.googleapis.com/v1beta/models/gemini-pro:generateContent?key=" +
          GEMINI_API_KEY,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            contents: [
              {
                parts: [{ text: prompt }],
              },
            ],
          }),
        }
      );

      const json = await res.json();
      const output = json.candidates?.[0]?.content?.parts?.[0]?.text || "";

      const bulletPoints = output.split("\n").filter((line) => line.startsWith("‚Ä¢") || line.startsWith("-"));
      setAiSummary(bulletPoints.length > 0 ? bulletPoints : ["No summary available."]);
    } catch (err) {
      console.error("Gemini API error:", err);
      setAiSummary(["AI Summary could not be generated."]);
    }
  };

  return (
    <div className="dashboard-container">
      <h1 className="dashboard-title">üåç India News Dashboard</h1>

      <div className="dashboard-panel-buttons">
        {["Live News Feed", "AI-Analysis Summary", "Recent Alerts Panel"].map((panel) => (
          <button
            key={panel}
            className={`dashboard-button ${
              activePanel === panel ? "dashboard-active" : ""
            }`}
            onClick={() => setActivePanel(panel)}
          >
            {panel}
          </button>
        ))}
      </div>

      <div className="dashboard-panel-content">
        {activePanel === "Live News Feed" && (
          <div className="dashboard-news-feed">
            {news.length > 0 ? (
              news.slice(0, 6).map((article, idx) => (
                <div className="dashboard-news-card" key={idx}>
                  <h3>{article.title}</h3>
                  <p>{article.description?.slice(0, 100)}...</p>
                  <a href={article.link} target="_blank" rel="noopener noreferrer">
                    Read More ‚Üí
                  </a>
                </div>
              ))
            ) : (
              <p>Loading news...</p>
            )}
          </div>
        )}

        {activePanel === "AI-Analysis Summary" && (
          <div className="dashboard-ai-panel">
            <h2>üß† AI-Based Analysis</h2>
            <ul className="ai-summary-list">
              {aiSummary.map((point, idx) => (
                <li key={idx}>{point}</li>
              ))}
            </ul>
          </div>
        )}

        {activePanel === "Recent Alerts Panel" && (
          <div className="dashboard-alert-panel">
            <h2>üö® Recent Alerts</h2>
            {alerts.length > 0 ? (
              alerts.map((alert, idx) => (
                <div className="dashboard-alert-card" key={idx}>
                  <h4>{alert.title}</h4>
                  <p>{alert.description?.slice(0, 100)}...</p>
                  <a href={alert.link} target="_blank" rel="noopener noreferrer">
                    Read More ‚Üí
                  </a>
                </div>
              ))
            ) : (
              <p>No recent alerts detected in India.</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
