import React, { useState, useEffect } from "react";
import { FaDownload, FaSearch, FaExclamationTriangle, FaCheckCircle } from "react-icons/fa";
import "./AlertsReports.css";

const AlertsReports = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [filter, setFilter] = useState("All");
  const [selectedCountry, setSelectedCountry] = useState("");
  const [selectedState, setSelectedState] = useState("");
  const [selectedCity, setSelectedCity] = useState("");
  const [alerts, setAlerts] = useState([]);
  const [locations, setLocations] = useState({});
  const [dateFilter, setDateFilter] = useState("All"); // New Date Filter
  const [specificDate, setSpecificDate] = useState(""); // For user input date

  useEffect(() => {
    const fetchRealNews = async () => {
      const alertLevels = ["High", "Medium", "Low"];
      const statuses = ["Ongoing", "Monitored", "Resolved"];
      const descriptions = [
        "Political unrest due to elections.",
        "Natural disaster warning in the region.",
        "Cyberattack targeting government infrastructure.",
        "Protests erupt due to new laws.",
        "Economic instability causing market fluctuations.",
        "Border tensions increasing military activity.",
        "Public health emergency declared.",
      ];

      let realNews = [];
      let startYear = 2020;

      for (let year = startYear; year <= 2025; year++) {
        for (let i = 0; i < 20; i++) {
          realNews.push({
            id: realNews.length + 1,
            date: `${year}-${String(Math.floor(Math.random() * 12) + 1).padStart(2, "0")}-${String(Math.floor(Math.random() * 28) + 1).padStart(2, "0")}`,
            country: "USA",
            state: "State A",
            city: "City X",
            level: alertLevels[Math.floor(Math.random() * alertLevels.length)],
            description: descriptions[Math.floor(Math.random() * descriptions.length)],
            status: statuses[Math.floor(Math.random() * statuses.length)],
          });
        }
      }

      setAlerts(realNews);
    };

    fetchRealNews();
  }, []);

  // Function to filter alerts based on date
  const filterByDate = (alert) => {
    const alertDate = new Date(alert.date);
    const today = new Date();
    
    if (dateFilter === "Last Week") {
      const oneWeekAgo = new Date();
      oneWeekAgo.setDate(today.getDate() - 7);
      return alertDate >= oneWeekAgo;
    } 
    if (dateFilter === "Last Month") {
      const oneMonthAgo = new Date();
      oneMonthAgo.setMonth(today.getMonth() - 1);
      return alertDate >= oneMonthAgo;
    }
    if (dateFilter === "Before Date" && specificDate) {
      return alertDate < new Date(specificDate);
    }
    
    return true; // Default case: show all
  };

  const filteredAlerts = alerts.filter((alert) =>
    (selectedCountry === "" || alert.country === selectedCountry) &&
    (selectedState === "" || alert.state === selectedState) &&
    (selectedCity === "" || alert.city === selectedCity) &&
    (filter === "All" || alert.level === filter) &&
    alert.description.toLowerCase().includes(searchTerm.toLowerCase()) &&
    filterByDate(alert)
  );

  return (
    <div className="alerts-reports-container">
      <h1 className="title">üåç Global Geopolitical Alerts & Reports</h1>

      <div className="controls">
        <div className="search-container">
          <FaSearch className="search-icon" />
          <input
            type="text"
            placeholder="Search alerts..."
            className="search-bar"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>

        <select className="filter-dropdown" value={filter} onChange={(e) => setFilter(e.target.value)}>
          <option value="All">All Levels</option>
          <option value="High">High Risk</option>
          <option value="Medium">Medium Risk</option>
          <option value="Low">Low Risk</option>
        </select>

        <select className="filter-dropdown" value={dateFilter} onChange={(e) => setDateFilter(e.target.value)}>
          <option value="All">All Dates</option>
          <option value="Last Week">Last Week</option>
          <option value="Last Month">Last Month</option>
          <option value="Before Date">Before Date</option>
        </select>

        {dateFilter === "Before Date" && (
          <input
            type="date"
            className="date-picker"
            value={specificDate}
            onChange={(e) => setSpecificDate(e.target.value)}
          />
        )}
      </div>

      <table className="alerts-table">
        <thead>
          <tr>
            <th>Date</th>
            <th>Country</th>
            <th>State</th>
            <th>City</th>
            <th>Alert Level</th>
            <th>Description</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {filteredAlerts.length > 0 ? (
            filteredAlerts.map((alert) => (
              <tr key={alert.id} className={alert.level.toLowerCase()}>
                <td>{alert.date}</td>
                <td>{alert.country}</td>
                <td>{alert.state}</td>
                <td>{alert.city}</td>
                <td>
                  {alert.level === "High" && <FaExclamationTriangle className="high-icon" />}
                  {alert.level === "Medium" && <FaExclamationTriangle className="medium-icon" />}
                  {alert.level === "Low" && <FaCheckCircle className="low-icon" />}
                  {alert.level}
                </td>
                <td>{alert.description}</td>
                <td>{alert.status}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="7" className="no-alerts">No alerts found.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default AlertsReports;
