import React, { useState, useEffect } from 'react';
import './Category.css';

const CategoryCarousel = ({ categories }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    if (categories && categories.length > 0) {
      // Initialize with the first category if categories are available
      setCurrentIndex(0);
    }
  }, [categories]);

  const nextSlide = () => {
    if (categories && categories.length > 0) {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % categories.length);
    }
  };

  const prevSlide = () => {
    if (categories && categories.length > 0) {
      setCurrentIndex((prevIndex) =>
        prevIndex === 0 ? categories.length - 1 : prevIndex - 1
      );
    }
  };

  // Check if categories is defined and has items
  if (!categories || categories.length === 0) {
    return <div>No categories available</div>;
  }

  return (
    <div className="carousel-container">
      <button className="prev" onClick={prevSlide}>
        &#10094;
      </button>
      <div className="carousel-items">
        {/* Display the current category image */}
        <img
          src={categories[currentIndex]?.image || ''}
          alt={categories[currentIndex]?.name || 'Category'}
          className="carousel-image"
        />
        <div className="carousel-text">
          <h2>{categories[currentIndex]?.name}</h2>
          <p>{categories[currentIndex]?.description}</p>
        </div>
      </div>
      <button className="next" onClick={nextSlide}>
        &#10095;
      </button>
    </div>
  );
};

export default Category.css
