import React, { useState } from 'react';
import './Category.css';

const CategoryCarousel = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const categories = [
    { image: '/images/image1.jpg', title: 'Category 1', description: 'Description for Category 1' },
    { image: '/images/image2.jpg', title: 'Category 2', description: 'Description for Category 2' },
    { image: '/images/image3.jpg', title: 'Category 3', description: 'Description for Category 3' },
    { image: '/images/image4.jpg', title: 'Category 4', description: 'Description for Category 4' },
    { image: '/images/image5.jpg', title: 'Category 5', description: 'Description for Category 5' },
    { image: '/images/image6.jpg', title: 'Category 6', description: 'Description for Category 6' },
    { image: '/images/image7.jpg', title: 'Category 7', description: 'Description for Category 7' },
    { image: '/images/image8.jpg', title: 'Category 8', description: 'Description for Category 8' }
  ];

  const goToNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % categories.length);
  };

  const goToPrev = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + categories.length) % categories.length);
  };

  const currentCategory = categories[currentIndex];

  return (
    <div className="category-carousel">
      <button className="carousel-btn prev-btn" onClick={goToPrev}>◄</button>
      
      <div className="carousel-content">
        <img src={currentCategory.image} alt={currentCategory.title} className="carousel-image" />
        <div className="carousel-info">
          <h2 className="carousel-title">{currentCategory.title}</h2>
          <p className="carousel-description">{currentCategory.description}</p>
        </div>
      </div>

      <button className="carousel-btn next-btn" onClick={goToNext}>►</button>
    </div>
  );
};

export default CategoryCarousel;
