import React, { useState } from "react";
import "./DataCollection.css";

const DataCollection = () => {
  const [sources, setSources] = useState([""]);
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState("");
  const [error, setError] = useState("");

  const handleInputChange = (index, value) => {
    const updatedSources = [...sources];
    updatedSources[index] = value;
    setSources(updatedSources);
  };

  const handleSelect = async (index) => {
    const selectedUrl = sources[index];

    if (!selectedUrl.startsWith("https://")) {
      alert("Please enter a valid URL!");
      return;
    }

    try {
      setLoading(true);
      setResult("");
      setError("");

      // Fetch raw HTML from the news article
      const response = await fetch(selectedUrl);
      const html = await response.text();

      // Extract visible text using DOMParser
      const parser = new DOMParser();
      const doc = parser.parseFromString(html, "text/html");

      // Get only the visible content
      const content = doc.body.innerText;

      // Send to backend (Gemini API)
      const analysisResponse = await fetch("http://localhost:5000/analyze", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ content }),
      });

      const data = await analysisResponse.json();
      if (data.summary) {
        setResult(data.summary);
      } else {
        setError("No summary returned from Gemini.");
      }
    } catch (err) {
      console.error("Error:", err);
      setError("Failed to fetch or analyze content.");
    } finally {
      setLoading(false);
    }
  };

  const addSource = () => {
    setSources([...sources, ""]);
  };

  return (
    <div className="data-collection">
      <h2 className="title">Early Warning News Analysis Dashboard</h2>

      <div className="sources-container">
        {sources.map((source, index) => (
          <div key={index} className="source-input">
            <input
              type="text"
              value={source}
              onChange={(e) => handleInputChange(index, e.target.value)}
              placeholder={`Enter News URL ${index + 1}`}
            />
            <button className="select-button" onClick={() => handleSelect(index)}>
              Analyze
            </button>
          </div>
        ))}
      </div>

      <div className="add-source-container">
        <button className="add-source" onClick={addSource}>
          + Add New Source
        </button>
      </div>

      {loading && <p className="loading">‚è≥ Analyzing...</p>}
      {error && <p className="error">‚ùå {error}</p>}
      {result && (
        <div className="analysis-result">
          <h3>üß† Gemini Summary:</h3>
          <pre>{result}</pre>
        </div>
      )}
    </div>
  );
};

export default DataCollection;
