import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import Register from './Register';
import Login from './Login';
import './Navbar.css'; // Import your CSS file for styling

const Navbar = () => {
  const [showRegister, setShowRegister] = useState(false); 
  const [showLogin, setShowLogin] = useState(false);

  const location = useLocation(); // Get current route

  // Open the Register form modal
  const handleSignUpClick = () => {
    setShowRegister(true);
  };

  // Close the Register form modal
  const handleCloseRegister = () => {
    setShowRegister(false);
  };

  // Open the Login form modal
  const handleLoginClick = () => {
    setShowLogin(true);
  };

  // Close the Login form modal
  const handleCloseLogin = () => {
    setShowLogin(false);
  };

  return (
    <div>
      <nav className="navbar">
        <div className="navbar-brand">
          <img src="/li.jpg.png" alt="Health&me logo" className="logo" />
        </div>
        <div className="navbar-links">
          <Link to="/" className={location.pathname === '/' ? 'active' : ''}>Home</Link>
          <Link to="/about-us" className={location.pathname === '/dashboard' ? 'active' : ''}>Dashboard</Link>
          <Link to="/category" className={location.pathname === '/' ? 'active' : ''}>Category</Link>
          <Link to="/articles" className={location.pathname === '/articles' ? 'active' : ''}>Articles</Link>
          <Link to="/stories" className={location.pathname === '/stories' ? 'active' : ''}>Stories</Link>
          <Link to="/recipes" className={location.pathname === '/recipes' ? 'active' : ''}>Recipes</Link>
        </div>
        <Link to="/search">
            <Link to="/search" className={location.pathname === '/search' ? 'active' : ''}></Link>
            <img src="tr.png" alt="Search" className="search-icon" />
        </Link>
        <div className="navbar-actions">
          <button className="login-button" onClick={handleLoginClick}>
            Login
          </button>
          <button className="signup-button" onClick={handleSignUpClick}>
            Sign Up
          </button>
            <Link to="/profile" className={location.pathname === '/profile' ? 'active' : ''}>Profile</Link>
        </div>
      </nav>

      {/* Conditional rendering of modals */}
      {showRegister && <Register onClose={handleCloseRegister} />}
      {showLogin && <Login onClose={handleCloseLogin} />}
    </div>
  );
};

export default Navbar;
