import React, { useState, useEffect } from "react";
import "./Dashboard.css"; // Ensure you have appropriate styles

const Dashboard = () => {
  const [countries, setCountries] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState("");
  const [activePanel, setActivePanel] = useState("Live News Feed");
  const [newsArticles, setNewsArticles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Fetch country list
  useEffect(() => {
    fetch("https://restcountries.com/v3.1/all")
      .then((response) => response.json())
      .then((data) => {
        const countryNames = data.map((country) => country.name.common).sort();
        setCountries(countryNames);
      })
      .catch((error) => console.error("Error fetching countries:", error));
  }, []);

  // Fetch news articles when selectedCountry or activePanel changes
  useEffect(() => {
    if (selectedCountry && activePanel === "Live News Feed") {
      const fetchNews = async () => {
        setLoading(true);
        setError(null);
        try {
          const response = await fetch(
            `https://newsapi.org/v2/top-headlines?country=${selectedCountry}&apiKey=YOUR_NEWSAPI_KEY`
          );
          const data = await response.json();
          if (data.status === "ok") {
            setNewsArticles(data.articles);
          } else {
            setError(data.message);
          }
        } catch (err) {
          setError("Failed to fetch news articles.");
        } finally {
          setLoading(false);
        }
      };

      fetchNews();
    }
  }, [selectedCountry, activePanel]);

  return (
    <div className="dashboard">
      <h1 className="title">Dashboard</h1>

      {/* Country Dropdown */}
      <div className="dropdown-container">
        <label htmlFor="country-select">Select a Country:</label>
        <select
          id="country-select"
          className="dropdown"
          value={selectedCountry}
          onChange={(e) => setSelectedCountry(e.target.value)}
        >
          <option value="">-- Choose a Country --</option>
          {countries.map((country, index) => (
            <option key={index} value={country}>
              {country}
            </option>
          ))}
        </select>
      </div>

      {/* Buttons */}
      <div className="buttons">
        {["Live News Feed", "AI-Analysis Summary", "Recent Alerts Panel"].map(
          (panel) => (
            <button
              key={panel}
              className={activePanel === panel ? "active" : ""}
              onClick={() => setActivePanel(panel)}
            >
              {panel}
            </button>
          )
        )}
      </div>

      {/* Content Display */}
      <div className="content">
        {activePanel === "Live News Feed" && (
          <div className="news-feed">
            {loading && <p>Loading news articles...</p>}
            {error && <p className="error">{error}</p>}
            {newsArticles.length > 0 ? (
              newsArticles.map((article, index) => (
                <div key={index} className="news-article">
                  <h2>{article.title}</h2>
                  <p>{article.description}</p>
                  <a
                    href={article.url}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    Read more
                  </a>
                </div>
              ))
            ) : (
              !loading && <p>No news articles available.</p>
            )}
          </div>
        )}
        {activePanel === "AI-Analysis Summary" && (
          <div className="ai-analysis-summary">
            {/* Placeholder for AI-Analysis Summary content */}
            <p>AI-Analysis Summary content goes here.</p>
          </div>
        )}
        {activePanel === "Recent Alerts Panel" && (
          <div className="recent-alerts-panel">
            {/* Placeholder for Recent Alerts Panel content */}
            <p>Recent Alerts Panel content goes here.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
