import React, { useState, useEffect } from "react";
import "./Dashboard.css";

const Dashboard = () => {
  const [news, setNews] = useState([]);
  const [activePanel, setActivePanel] = useState("Live News Feed");
  const [aiSummary, setAiSummary] = useState([]);
  const [alerts, setAlerts] = useState([]);

  const NEWS_API_KEY = "pub_788677aa36735d8282ee66b9146090ddf3d0f";
  const GEMINI_API_KEY = "AIzaSyCgz-p2aVMhAfPapKiuqpPk5fSU1mczYg";

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const res = await fetch(
          `https://newsdata.io/api/1/news?apikey=${NEWS_API_KEY}&language=en`
        );
        const data = await res.json();
        let articles = data.results || [];

        // Filter for unique titles
        const seenTitles = new Set();
        const uniqueArticles = articles.filter((article) => {
          if (!article.title || seenTitles.has(article.title)) return false;
          seenTitles.add(article.title);
          return true;
        });

        setNews(uniqueArticles);

        // Extract alerts
        const alertKeywords = [
          "alert",
          "emergency",
          "warning",
          "disaster",
          "evacuate",
          "flood",
          "fire",
          "storm"
        ];
        const filtered = uniqueArticles.filter((article) =>
          alertKeywords.some((keyword) =>
            (article.title + " " + article.description)
              .toLowerCase()
              .includes(keyword)
          )
        );
        setAlerts(filtered);
        generateAISummary(uniqueArticles.slice(0, 8));
      } catch (error) {
        console.error("Failed to fetch news:", error);
        setNews([]);
        setAiSummary([]);
        setAlerts([]);
      }
    };

    fetchNews();
  }, []);

  const generateAISummary = async (articles) => {
    const prompt = `Summarize the following news headlines in bullet points:\n\n${articles
      .map((a, i) => `${i + 1}. ${a.title}`)
      .join("\n")}`;

    try {
      const res = await fetch(
        `https://generativelanguage.googleapis.com/v1beta/models/gemini-pro:generateContent?key=${GEMINI_API_KEY}`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json"
          },
          body: JSON.stringify({
            contents: [
              {
                parts: [{ text: prompt }]
              }
            ]
          })
        }
      );

      const json = await res.json();
      const output = json.candidates?.[0]?.content?.parts?.[0]?.text || "";

      const bulletPoints = output
        .split("\n")
        .map((line) => line.trim())
        .filter(
          (line) =>
            line.startsWith("•") ||
            line.startsWith("-") ||
            /^\d+\./.test(line)
        );

      if (bulletPoints.length > 0) {
        setAiSummary(bulletPoints);
      } else if (output) {
        setAiSummary([output]);
      } else {
        const fallback = articles.map((a, i) => `${i + 1}. ${a.title}`);
        setAiSummary([`AI Summary unavailable. Top headlines:\n`, ...fallback]);
      }
    } catch (err) {
      console.error("Gemini API error:", err);
      setAiSummary(["❌ AI Summary could not be generated."]);
    }
  };

  // 52 Commonwealth country codes
  const commonwealthCountries = [
    "AU", "BD", "BW", "BZ", "CM", "CA", "CY", "DM", "FJ", "GM",
    "GH", "GD", "IN", "JM", "KE", "KI", "LS", "MW", "MY", "MV",
    "MT", "MU", "MZ", "NA", "NR", "NZ", "NG", "PK", "PG", "RW",
    "KN", "LC", "VC", "WS", "SC", "SL", "SG", "SB", "ZA", "LK",
    "SZ", "TZ", "TO", "TT", "TV", "UG", "GB", "VU", "ZM"
  ];

  // Get top 10 commonwealth country article counts
  const commonwealthDistribution = Object.entries(
    news.reduce((acc, article) => {
      const code = (article.country || "unknown").toUpperCase();
      if (commonwealthCountries.includes(code)) {
        acc[code] = (acc[code] || 0) + 1;
      }
      return acc;
    }, {})
  )
    .sort((a, b) => b[1] - a[1])
    .slice(0, 10);

  return (
    <div className="dashboard-container">
      <h1 className="dashboard-title">🌍 Global News Dashboard</h1>

      <div className="dashboard-controls">
        <p>🌐 Showing top global news (latest 16 unique headlines)</p>
      </div>

      <div className="dashboard-panel-buttons">
        {["Live News Feed", "AI-Analysis Summary", "Recent Alerts Panel"].map(
          (panel) => (
            <button
              key={panel}
              className={`dashboard-button ${
                activePanel === panel ? "dashboard-active" : ""
              }`}
              onClick={() => setActivePanel(panel)}
            >
              {panel}
            </button>
          )
        )}
      </div>

      <div className="dashboard-panel-content">
        {activePanel === "Live News Feed" && (
          <div className="dashboard-news-feed">
            {news.length > 0 ? (
              news.slice(0, 16).map((article, idx) => (
                <div className="dashboard-news-card" key={idx}>
                  <h3>{article.title}</h3>
                  <p>{article.description?.slice(0, 100)}...</p>
                  <a
                    href={article.link}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    Read More →
                  </a>
                </div>
              ))
            ) : (
              <p>Loading latest global news...</p>
            )}
          </div>
        )}

        {activePanel === "AI-Analysis Summary" && (
          <div className="dashboard-ai-panel">
            <h2>🧠 AI-Based Global News Summary</h2>
            <p className="ai-summary-intro">
              Here's a professional summary of the top news globally based on AI analysis:
            </p>
            <ul className="ai-summary-list">
              {aiSummary.map((point, idx) => (
                <li key={idx}>{point}</li>
              ))}
            </ul>

            <h3>🌐 Commonwealth Country-wise News Distribution (Top 10)</h3>
            <table className="ai-summary-table">
              <thead>
                <tr>
                  <th>Country</th>
                  <th>No. of Articles</th>
                </tr>
              </thead>
              <tbody>
                {commonwealthDistribution.length > 0 ? (
                  commonwealthDistribution.map(([countryCode, count], idx) => (
                    <tr key={idx}>
                      <td>{countryCode}</td>
                      <td>{count}</td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="2">No Commonwealth country news found.</td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        )}

        {activePanel === "Recent Alerts Panel" && (
          <div className="dashboard-alert-panel">
            <h2>🚨 Recent Alerts</h2>
            {alerts.length > 0 ? (
              alerts.map((alert, idx) => (
                <div className="dashboard-alert-card" key={idx}>
                  <h4>{alert.title}</h4>
                  <p>{alert.description?.slice(0, 100)}...</p>
                  <a
                    href={alert.link}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    Read More →
                  </a>
                </div>
              ))
            ) : (
              <p>No recent alerts detected in global headlines.</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
