sourcesContent:
import React, { useState } from 'react';
import './Articles.css'; // Import the CSS file for styling

const Article = () => {
  const [articles, setArticles] = useState([
    { 
      title: "The Importance of Flexibility in Fitness", 
      body: "Flexibility is an essential component of overall fitness...\n\nYoga is an excellent way to increase flexibility while also improving strength and balance.\n\nDynamic stretches before workouts and static stretches after workouts help improve range of motion.\n\nFoam rolling can also help release tight muscles and improve flexibility.\n\nHold each stretch for at least 30 seconds for maximum benefit.\n\nTry to stretch all major muscle groups to maintain balance.\n\nBreath deeply during stretching to help relax your muscles.\n\nBe patient with yourself; improving flexibility takes time.\n\nIncorporating flexibility exercises into your routine can enhance performance and reduce the risk of injury.", 
      category: "Exercise", date: "2024-10-05", isExpanded: false 
    },
    { 
      title: "The Power of Consistency in Fitness", 
      body: "Consistency is the key to achieving fitness goals...\n\nBuilding a workout routine and sticking to it will lead to long-term success.\n\nSmall, consistent efforts add up over time.\n\nDon’t expect quick results; fitness is a marathon, not a sprint.\n\nTracking your progress keeps you motivated and accountable.\n\nConsistency is about making healthy choices every day, not just on workout days.\n\nEven on rest days, focus on recovery, hydration, and nutrition.\n\nIt’s important to stay committed to your routine, even when progress seems slow.\n\nRemember, every step forward is a step closer to your goal.\n\nThe power of consistency will pay off in the long run.", 
      category: "Fitness", date: "2024-10-04", isExpanded: false 
    },
    { 
      title: "The Role of Supplements in Fitness", 
      body: "Supplements can support your fitness goals, but they should not replace a balanced diet...\n\nProtein powders help supplement protein intake for muscle growth and recovery.\n\nCreatine can enhance strength and power during high-intensity exercises.\n\nBranched-Chain Amino Acids (BCAAs) help reduce muscle soreness after workouts.\n\nPre-workout supplements can increase energy and focus during exercise.\n\nPost-workout supplements like glutamine aid in muscle recovery.\n\nVitamins and minerals help fill nutritional gaps in your diet.\n\nAlways consult a healthcare professional before taking any supplements.\n\nSupplements should be used in conjunction with a well-balanced diet and exercise program.\n\nFocus on whole foods first and use supplements to fill in the gaps when needed.\n\nSupplements can enhance performance, but consistency in your workout routine is key.", 
      category: "Nutrition", date: "2024-10-03", isExpanded: false 
    },
    { 
      title: "Yoga for Stress Relief", 
      body: "Yoga is a powerful practice for relieving stress and improving mental health...\n\nBreathing exercises in yoga help activate the parasympathetic nervous system to reduce stress.\n\nThe physical postures, or asanas, help release tension and promote relaxation.\n\nYoga encourages mindfulness, which helps you stay present and calm.\n\nRegular practice can lower cortisol levels, the body's stress hormone.\n\nGentle yoga practices like Hatha and Restorative yoga are great for stress relief.\n\nDeep stretches also help release stress stored in the muscles.\n\nYoga can improve sleep quality, which is essential for managing stress.\n\nIncorporating yoga into your daily routine helps build resilience to stress.\n\nYou don’t need to be flexible to practice yoga; it’s about relaxation and connection.", 
      category: "Fitness", date: "2024-10-02", isExpanded: false 
    },
  ]);
  
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [title, setTitle] = useState('');
  const [body, setBody] = useState('');
  const [category, setCategory] = useState('Nutrition');
  
  // Function to toggle the modal visibility
  const openModal = () => setIsModalOpen(true);
  const closeModal = () => setIsModalOpen(false);

  // Handle the submission of a new article
  const handleSubmit = (e) => {
    e.preventDefault();
    const newArticle = {
      title,
      body,
      category,
      date: new Date().toLocaleDateString(),
      isExpanded: false,
    };
    setArticles([newArticle, ...articles]);
    setTitle('');
    setBody('');
    setCategory('Nutrition');
    closeModal(); // Close the modal after submission
  };

  // Handle expanding an article to show full content
  const toggleExpand = (index) => {
    const updatedArticles = [...articles];
    updatedArticles[index].isExpanded = !updatedArticles[index].isExpanded;
    setArticles(updatedArticles);
  };

  return (
    <div className="article-container">
      <h1>Articles on Health, Fitness, and Nutrition</h1>
      
      {/* Article Tiles */}
      <div className="articles-tiles">
        {articles.slice(0, 10).map((article, index) => (
          <div className="article-tile" key={index}>
            <h3>{article.title}</h3>
            <p className="article-date">{article.date}</p>
            <p>{article.category}</p>
            <div className="article-body">
              {article.isExpanded ? (
                <p>{article.body}</p>
              ) : (
                <p>{article.body.slice(0, 100)}... <span onClick={() => toggleExpand(index)} className="read-more">Read More</span></p>
              )}
            </div>
          </div>
        ))}
      </div>

      {/* Post Your Own Article Button */}
      <button className="post-article-btn" onClick={openModal}>Post Your Own Article</button>

      {/* Modal for Posting Article */}
      {isModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <button className="close-modal-btn" onClick={closeModal}>X</button>
            <form onSubmit={handleSubmit}>
              <h3>Publish Your Article</h3>
              <p><h9>Please enter title of your Article</h9>
              <input
                type="text"
                placeholder="Article Title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                required
              />
              </p>
              <p><h10>Please enter content of your Article</h10></p>
              <textarea
                placeholder="Write your article content..."
                value={body}
                onChange={(e) => setBody(e.target.value)}
                required
              />
              <p>
              <button type="submit">Publish Article</button>
              </p>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Article;
