import React, { useState } from 'react';
import './CascadingLists.css';

const CascadingLists = () => {
  const [selectedGoal, setSelectedGoal] = useState('');
  const [selectedWorkout, setSelectedWorkout] = useState('');

  const workoutOptions = {
    WeightLoss: ['Cardio Blast', 'High-Intensity Interval Training', 'Zumba', 'Kickboxing'],
    MuscleGain: ['Weight Lifting', 'Bodybuilding', 'Resistance Training'],
    Flexibility: ['Yoga', 'Pilates', 'Dynamic Stretching'],
  };

  const handleGoalChange = (event) => {
    setSelectedGoal(event.target.value);
    setSelectedWorkout(''); // Reset the workout selection when the goal changes
  };

  return (
    <div className="cascading-container">
      <h2>Select Your Fitness Goal and Workout</h2>
      <div className="dropdown-container">
        <label htmlFor="goal">Fitness Goal:</label>
        <select id="goal" value={selectedGoal} onChange={handleGoalChange}>
          <option value="">Select a goal</option>
          <option value="WeightLoss">Weight Loss</option>
          <option value="MuscleGain">Muscle Gain</option>
          <option value="Flexibility">Flexibility</option>
        </select>
      </div>

      {selectedGoal && (
        <div className="dropdown-container">
          <label htmlFor="workout">Select Workout:</label>
          <select
            id="workout"
            value={selectedWorkout}
            onChange={(e) => setSelectedWorkout(e.target.value)}
          >
            <option value="">Select a workout</option>
            {workoutOptions[selectedGoal].map((workout, index) => (
              <option key={index} value={workout}>
                {workout}
              </option>
            ))}
          </select>
        </div>
      )}

      {selectedWorkout && (
        <div className="selected-workout">
          <p>You have selected: <strong>{selectedWorkout}</strong></p>
        </div>
      )}
    </div>
  );
};

export default CascadingLists;
