import React, { useState, useEffect } from "react";
import "./Dashboard.css";

const Dashboard = () => {
  const [news, setNews] = useState([]);
  const [activePanel, setActivePanel] = useState("Live News Feed");
  const [aiSummary, setAiSummary] = useState([]);
  const [alerts, setAlerts] = useState([]);

  const NEWS_API_KEY = "pub_788677aa36735d8282ee66b9146090ddf3d0f";
  const GEMINI_API_KEY = "AIzaSyCgz-p2aVMhAfPapKiuqpPk5fSU1mczYg";

  const commonwealthCountries = [
    "IN", "AU", "CA", "GB", "NZ", "ZA", "PK", "NG", "MY", "SG", "BD", "LK", "KE", "UG",
    "GH", "TZ", "ZM", "ZW", "JM", "CY", "BW", "NA", "MW", "FJ", "SL", "GM", "RW", "CM",
    "MZ", "SZ", "BN", "AG", "BB", "BZ", "DM", "GD", "GY", "KI", "LS", "MV", "MT", "MU",
    "NR", "KN", "LC", "VC", "SB", "TO", "TT", "TV", "VU"
  ];

  const countryCodeToName = {
    IN: "India", AU: "Australia", CA: "Canada", GB: "United Kingdom", NZ: "New Zealand",
    ZA: "South Africa", PK: "Pakistan", NG: "Nigeria", MY: "Malaysia", SG: "Singapore",
    BD: "Bangladesh", LK: "Sri Lanka", KE: "Kenya", UG: "Uganda", GH: "Ghana",
    TZ: "Tanzania", ZM: "Zambia", ZW: "Zimbabwe", JM: "Jamaica", CY: "Cyprus",
    BW: "Botswana", NA: "Namibia", MW: "Malawi", FJ: "Fiji", SL: "Sierra Leone",
    GM: "Gambia", RW: "Rwanda", CM: "Cameroon", MZ: "Mozambique", SZ: "Eswatini",
    BN: "Brunei", AG: "Antigua and Barbuda", BB: "Barbados", BZ: "Belize",
    DM: "Dominica", GD: "Grenada", GY: "Guyana", KI: "Kiribati", LS: "Lesotho",
    MV: "Maldives", MT: "Malta", MU: "Mauritius", NR: "Nauru",
    KN: "Saint Kitts and Nevis", LC: "Saint Lucia", VC: "Saint Vincent and the Grenadines",
    SB: "Solomon Islands", TO: "Tonga", TT: "Trinidad and Tobago", TV: "Tuvalu",
    VU: "Vanuatu"
  };

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const res = await fetch(
          `https://newsdata.io/api/1/news?apikey=${NEWS_API_KEY}&language=en`
        );
        const data = await res.json();
        let articles = data.results || [];

        const seenTitles = new Set();
        const uniqueArticles = articles.filter((article) => {
          if (!article.title || seenTitles.has(article.title)) return false;
          seenTitles.add(article.title);
          return true;
        });

        setNews(uniqueArticles);

        const alertKeywords = [
          "alert", "emergency", "warning", "disaster", "evacuate",
          "flood", "fire", "storm"
        ];
        const filtered = uniqueArticles.filter((article) =>
          alertKeywords.some((keyword) =>
            (article.title + " " + article.description)
              .toLowerCase()
              .includes(keyword)
          )
        );
        setAlerts(filtered);
        generateAISummary(uniqueArticles.slice(0, 10));
      } catch (error) {
        console.error("Failed to fetch news:", error);
        setNews([]);
        setAiSummary([]);
        setAlerts([]);
      }
    };

    fetchNews();
  }, []);

  const generateAISummary = async (articles) => {
    const prompt = `Summarize the following news headlines in bullet points:\n\n${articles
      .map((a, i) => `${i + 1}. ${a.title}`)
      .join("\n")}`;

    try {
      const res = await fetch(
        `https://generativelanguage.googleapis.com/v1beta/models/gemini-pro:generateContent?key=${GEMINI_API_KEY}`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json"
          },
          body: JSON.stringify({
            contents: [
              {
                parts: [{ text: prompt }]
              }
            ]
          })
        }
      );

      const json = await res.json();
      const output = json.candidates?.[0]?.content?.parts?.[0]?.text || "";

      const bulletPoints = output
        .split("\n")
        .map((line) => line.trim())
        .filter(
          (line) =>
            line.startsWith("‚Ä¢") ||
            line.startsWith("-") ||
            /^\d+\./.test(line)
        );

      if (bulletPoints.length > 0) {
        setAiSummary(bulletPoints);
      } else if (output) {
        setAiSummary([output]);
      } else {
        const fallback = articles.map((a, i) => `${i + 1}. ${a.title}`);
        setAiSummary([`AI Summary unavailable. Top headlines:\n`, ...fallback]);
      }
    } catch (err) {
      console.error("Gemini API error:", err);
      setAiSummary(["‚ùå AI Summary could not be generated."]);
    }
  };

  const getCommonwealthDistribution = () => {
    const distribution = news.reduce((acc, article) => {
      const country = Array.isArray(article.country)
        ? article.country[0]
        : typeof article.country === "string"
        ? article.country
        : "unknown";

      const code = country?.toUpperCase?.() || "unknown";

      if (commonwealthCountries.includes(code)) {
        const name = countryCodeToName[code] || code;
        acc[name] = (acc[name] || 0) + 1;
      }

      return acc;
    }, {});

    return Object.entries(distribution)
      .sort((a, b) => b[1] - a[1])
      .slice(0, 10);
  };

  return (
    <div className="dashboard-container">
      <h1 className="dashboard-title">üåç Global News Dashboard</h1>

      <div className="dashboard-controls">
        <p>üåê Showing top global news (latest 16 unique headlines)</p>
      </div>

      <div className="dashboard-panel-buttons">
        {["Live News Feed", "AI-Analysis Summary", "Recent Alerts Panel"].map(
          (panel) => (
            <button
              key={panel}
              className={`dashboard-button ${
                activePanel === panel ? "dashboard-active" : ""
              }`}
              onClick={() => setActivePanel(panel)}
            >
              {panel}
            </button>
          )
        )}
      </div>

      <div className="dashboard-panel-content">
        {activePanel === "Live News Feed" && (
          <div className="dashboard-news-feed">
            {news.length > 0 ? (
              news.slice(0, 16).map((article, idx) => (
                <div className="dashboard-news-card" key={idx}>
                  <h3>{article.title}</h3>
                  <p>{article.description?.slice(0, 100)}...</p>
                  <a
                    href={article.link}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    Read More ‚Üí
                  </a>
                </div>
              ))
            ) : (
              <p>Loading latest global news...</p>
            )}
          </div>
        )}

        {activePanel === "AI-Analysis Summary" && (
          <div className="dashboard-ai-panel">
            <h2>üß† AI-Based Global News Summary</h2>
            <p className="ai-summary-intro">
              Here's a professional summary of the top news globally based on AI analysis:
            </p>
            <ul className="ai-summary-list">
              {aiSummary.map((point, idx) => (
                <li key={idx}>{point}</li>
              ))}
            </ul>

            <h3>üåê Commonwealth Country News Distribution (Top 10)</h3>
            <table className="ai-summary-table">
              <thead>
                <tr>
                  <th>Country</th>
                  <th>No. of Articles</th>
                </tr>
              </thead>
              <tbody>
                {getCommonwealthDistribution().map(([country, count], idx) => (
                  <tr key={idx}>
                    <td>{country}</td>
                    <td>{count}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {activePanel === "Recent Alerts Panel" && (
          <div className="dashboard-alert-panel">
            <h2>üö® Recent Alerts</h2>
            {alerts.length > 0 ? (
              alerts.map((alert, idx) => (
                <div className="dashboard-alert-card" key={idx}>
                  <h4>{alert.title}</h4>
                  <p>{alert.description?.slice(0, 100)}...</p>
                  <a
                    href={alert.link}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    Read More ‚Üí
                  </a>
                </div>
              ))
            ) : (
              <p>No recent alerts detected in global headlines.</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
