import React, { useState } from 'react';
import './MyTrainer.css';

const MyTrainer = () => {
  // Initial trainer details
  const [trainer, setTrainer] = useState({
    name: 'Rajesh Kumar',
    experience: '8 years',
    certifications: ['Certified Personal Trainer', 'Yoga Specialist'],
    trainingStyle: 'Yoga and Functional Training',
    availability: 'Monday to Saturday, 7 AM - 8 PM',
  });

  // State to handle trainer change and reason
  const [newTrainerName, setNewTrainerName] = useState('');
  const [reason, setReason] = useState('');
  const [showForm, setShowForm] = useState(false);

  // Function to change trainer details
  const changeTrainerDetails = () => {
    if (newTrainerName && reason) {
      setTrainer(prevState => ({
        ...prevState,
        name: newTrainerName, // New trainer name
      }));
      setShowForm(false); // Hide the form after submission
    } else {
      alert('Please provide the new trainer name and reason');
    }
  };

  return (
    <div className="trainer-container">
      <h2>My Trainer</h2>
      
      {/* Display trainer details */}
      <div className="trainer-details">
        <h3>Name: {trainer.name}</h3>
        <p>Experience: {trainer.experience}</p>
        <p>Certifications: {trainer.certifications.join(', ')}</p>
        <p>Training Style: {trainer.trainingStyle}</p>
        <p>Availability: {trainer.availability}</p>
      </div>
      
      {/* Button to change trainer */}
      <button 
        onClick={() => setShowForm(true)} 
        className="change-trainer-btn">
        Change Trainer
      </button>

      {/* Form to change trainer */}
      {showForm && (
        <div className="change-trainer-form">
          <h3>Change Trainer</h3>
          <label htmlFor="newTrainerName">New Trainer Name:</label>
          <input
            type="text"
            id="newTrainerName"
            value={newTrainerName}
            onChange={(e) => setNewTrainerName(e.target.value)}
            placeholder="Enter new trainer's name"
          />

          <label htmlFor="reason">Reason for Change:</label>
          <textarea
            id="reason"
            value={reason}
            onChange={(e) => setReason(e.target.value)}
            placeholder="Enter your reason"
          ></textarea>

          <button onClick={changeTrainerDetails} className="submit-btn">
            Submit
          </button>
        </div>
      )}
    </div>
  );
};

export default MyTrainer;
