import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";

function AIAnalysis() {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const newsUrl = queryParams.get("url");
  const [analysisResult, setAnalysisResult] = useState(null);

  useEffect(() => {
    if (newsUrl) {
      fetch(`http://localhost/rag2/api/analyze.php?url=${encodeURIComponent(newsUrl)}`)
        .then((response) => response.json())
        .then((data) => setAnalysisResult(data))
        .catch((error) => console.error("Error fetching analysis:", error));
    }
  }, [newsUrl]);

  return (
    <div className="ai-analysis">
      <h2>AI News Analysis</h2>
      {analysisResult ? (
        <div className="analysis-results">
          <p><strong>URL:</strong> {analysisResult.url}</p>
          <p><strong>Title:</strong> {analysisResult.title}</p>
          <p><strong>Authenticity Score:</strong> {analysisResult.authenticity}</p>
          <p><strong>Summary:</strong> {analysisResult.summary}</p>
        </div>
      ) : (
        <p>Loading analysis...</p>
      )}
    </div>
  );
}

export default AIAnalysis;
