import React, { useState } from 'react';
import './MyTrainer.css';

const MyTrainer = () => {
  const [trainer, setTrainer] = useState({
    name: 'John Doe',
    experience: '5 years',
    certifications: ['Certified Personal Trainer', 'Nutrition Expert'],
    trainingStyle: 'Strength Training and Cardio',
    availability: 'Monday to Friday, 9 AM - 6 PM',
  });

  // Example function to update trainer's details dynamically
  const changeTrainerName = () => {
    setTrainer(prevState => ({
      ...prevState,
      name: 'Jane Smith', // Changing the trainer's name
    }));
  };

  return (
    <div className="trainer-container">
      <h2>My Trainer</h2>
      <div className="trainer-details">
        <h3>Name: {trainer.name}</h3>
        <p>Experience: {trainer.experience}</p>
        <p>Certifications: {trainer.certifications.join(', ')}</p>
        <p>Training Style: {trainer.trainingStyle}</p>
        <p>Availability: {trainer.availability}</p>
        <button onClick={changeTrainerName} className="change-name-btn">Change Trainer's Name</button>
      </div>
    </div>
  );
};

export default MyTrainer;
