import React, { useState } from 'react';
import './SystemSettings.css'; // Import CSS

const SystemSettings = () => {
  const [selectedSection, setSelectedSection] = useState('ai-parameters'); // Default section
  const [notifications, setNotifications] = useState({
    email: false,
    sms: false,
    push: false,
  });

  const toggleNotification = (type) => {
    setNotifications((prev) => ({
      ...prev,
      [type]: !prev[type],
    }));
  };

  return (
    <div className="settings-container">
      {/* Sidebar Navigation */}
      <aside className="settings-sidebar">
        <h2>System Settings</h2>
        <ul>
          <li>
            <button 
              className={selectedSection === 'ai-parameters' ? 'active' : ''}
              onClick={() => setSelectedSection('ai-parameters')}
            >
              AI Parameters
            </button>
          </li>
          <li>
            <button 
              className={selectedSection === 'security-settings' ? 'active' : ''}
              onClick={() => setSelectedSection('security-settings')}
            >
              Security Settings
            </button>
          </li>
          <li>
            <button 
              className={selectedSection === 'notifications' ? 'active' : ''}
              onClick={() => setSelectedSection('notifications')}
            >
              Notification Preferences
            </button>
          </li>
          <li>
            <button 
              className={selectedSection === 'early-warning' ? 'active' : ''}
              onClick={() => setSelectedSection('early-warning')}
            >
              Early Warning Dashboard
            </button>
          </li>
          <li>
            <button 
              className={selectedSection === 'alert-settings' ? 'active' : ''}
              onClick={() => setSelectedSection('alert-settings')}
            >
              Alert Settings
            </button>
          </li>
        </ul>
      </aside>

      {/* Main Content - Show only selected section */}
      <main className="settings-content">
        {selectedSection === 'ai-parameters' && (
          <section id="ai-parameters">
            <h3>AI Parameters</h3>
            <label>Sentiment Analysis Threshold:</label>
            <input type="range" min="0" max="100" />

            <label>Data Collection Frequency:</label>
            <select>
              <option>Daily</option>
              <option>Weekly</option>
              <option>Monthly</option>
            </select>
          </section>
        )}

        {selectedSection === 'security-settings' && (
          <section id="security-settings">
            <h3>Security Settings</h3>
            <label><input type="checkbox" /> Enable 2FA Authentication</label>
            <label><input type="text" placeholder="Enter Whitelisted IP" /></label>
          </section>
        )}

        {selectedSection === 'notifications' && (
          <section id="notifications">
            <h3>Notification Preferences</h3>
            <div className="toggle-container">
              <span>Email Notifications</span>
              <button 
                className={`toggle-btn ${notifications.email ? 'active' : ''}`} 
                onClick={() => toggleNotification('email')}
              >
                {notifications.email ? 'ON' : 'OFF'}
              </button>
            </div>
            <div className="toggle-container">
              <span>SMS Notifications</span>
              <button 
                className={`toggle-btn ${notifications.sms ? 'active' : ''}`} 
                onClick={() => toggleNotification('sms')}
              >
                {notifications.sms ? 'ON' : 'OFF'}
              </button>
            </div>
            <div className="toggle-container">
              <span>Push Notifications</span>
              <button 
                className={`toggle-btn ${notifications.push ? 'active' : ''}`} 
                onClick={() => toggleNotification('push')}
              >
                {notifications.push ? 'ON' : 'OFF'}
              </button>
            </div>
          </section>
        )}

        {selectedSection === 'early-warning' && (
          <section id="early-warning">
            <h3>Early Warning Dashboard</h3>
            <label><input type="checkbox" /> Enable Early Warnings</label>
            <label>Threshold Limit:</label>
            <input type="number" min="1" max="100" />
            <label>Notification Method:</label>
            <select>
              <option>Email</option>
              <option>SMS</option>
              <option>Push Notification</option>
            </select>
          </section>
        )}

        {selectedSection === 'alert-settings' && (
          <section id="alert-settings">
            <h3>Alert Settings</h3>
            <label><input type="checkbox" /> Enable Critical Alerts</label>
            <label>Alert Sound:</label>
            <select>
              <option>Beep</option>
              <option>Chime</option>
              <option>Silent</option>
            </select>
            <label>Alert Frequency:</label>
            <select>
              <option>Instant</option>
              <option>Every 10 Minutes</option>
              <option>Hourly</option>
            </select>
          </section>
        )}
      </main>
    </div>
  );
};

export default SystemSettings;
