import React, { useState, useEffect } from "react";

const Dashboard = () => {
  const [countries, setCountries] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState("");

  // Fetch countries from REST API
  useEffect(() => {
    fetch("https://restcountries.com/v3.1/all")
      .then((response) => response.json())
      .then((data) => {
        const countryNames = data.map((country) => country.name.common).sort();
        setCountries(countryNames);
      })
      .catch((error) => console.error("Error fetching countries:", error));
  }, []);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-900 text-white p-6">
      {/* Heading */}
      <h1 className="text-4xl font-bold mb-6 text-orange-400">Dashboard</h1>

      {/* Country Dropdown */}
      <div className="mb-6">
        <label className="block text-lg mb-2">Select a Country:</label>
        <select
          className="w-80 p-3 bg-gray-800 text-white rounded-md"
          value={selectedCountry}
          onChange={(e) => setSelectedCountry(e.target.value)}
        >
          <option value="">-- Choose a Country --</option>
          {countries.map((country, index) => (
            <option key={index} value={country}>
              {country}
            </option>
          ))}
        </select>
      </div>

      {/* Buttons */}
      <div className="flex space-x-4">
        <button className="px-6 py-3 bg-blue-600 hover:bg-blue-700 rounded-lg shadow-md">
          Live News Feed
        </button>
        <button className="px-6 py-3 bg-green-600 hover:bg-green-700 rounded-lg shadow-md">
          AI-Analysis Summary
        </button>
        <button className="px-6 py-3 bg-red-600 hover:bg-red-700 rounded-lg shadow-md">
          Recent Alerts Panel
        </button>
      </div>
    </div>
  );
};

export default Dashboard;
