import React, { useState, useEffect } from "react";
import "./Dashboard.css";

const Dashboard = () => {
  const [selectedCountry, setSelectedCountry] = useState("");
  const [activePanel, setActivePanel] = useState("Live News Feed");
  const [news, setNews] = useState([]);

  const API_KEY = "pub_788677aa36735d8282ee66b9146090ddf3d0f";

  const countryList = [
    { name: "India", code: "IN" },
    { name: "United States", code: "US" },
    { name: "United Kingdom", code: "GB" },
    { name: "Australia", code: "AU" },
    { name: "Germany", code: "DE" },
    { name: "Canada", code: "CA" },
    { name: "France", code: "FR" },
    { name: "Japan", code: "JP" },
    { name: "China", code: "CN" },
    { name: "Russia", code: "RU" }
  ];

  const getCountryCode = (name) => {
    const found = countryList.find(c => c.name === name);
    return found ? found.code.toLowerCase() : "";
  };

  useEffect(() => {
    const fetchNews = async () => {
      if (selectedCountry) {
        const countryCode = getCountryCode(selectedCountry);
        try {
          const response = await fetch(
            `https://newsdata.io/api/1/news?apikey=${API_KEY}&country=${countryCode}&language=en`
          );
          const data = await response.json();
          setNews(data.results || []);
        } catch (err) {
          console.error("Failed to fetch news:", err);
          setNews([]);
        }
      }
    };
    fetchNews();
  }, [selectedCountry]);

  // ‚ú® Extract AI Summary from Top Headlines
  const generateAISummary = () => {
    if (news.length === 0) return "No data to summarize.";

    const keywords = news
      .slice(0, 6)
      .map(article => article.title)
      .flat()
      .join(". ")
      .replace(/[^a-zA-Z0-9. ]/g, "");

    return `Based on recent news, major highlights include: ${keywords}`;
  };

  // ‚ú® Filter alerts (keywords: alert, emergency, disaster, warning, etc.)
  const getAlerts = () => {
    const alertKeywords = ["alert", "emergency", "warning", "disaster", "evacuate", "flood", "fire", "storm"];
    return news.filter(article =>
      alertKeywords.some(keyword =>
        (article.title + " " + article.description).toLowerCase().includes(keyword)
      )
    );
  };

  const aiSummary = generateAISummary();
  const recentAlerts = getAlerts();

  return (
    <div className="dashboard-container">
      <h1 className="dashboard-title">üåç Dashboard</h1>

      <div className="dashboard-dropdown-container">
        <label htmlFor="country">Select a Country:</label>
        <select
          id="country"
          value={selectedCountry}
          onChange={(e) => setSelectedCountry(e.target.value)}
        >
          <option value="">-- Choose a Country --</option>
          {countryList.map((country, index) => (
            <option key={index} value={country.name}>{country.name}</option>
          ))}
        </select>
      </div>

      <div className="dashboard-panel-buttons">
        {["Live News Feed", "AI-Analysis Summary", "Recent Alerts Panel"].map((panel) => (
          <button
            key={panel}
            className={`dashboard-button ${activePanel === panel ? "dashboard-active" : ""}`}
            onClick={() => setActivePanel(panel)}
          >
            {panel}
          </button>
        ))}
      </div>

      <div className="dashboard-panel-content">
        {activePanel === "Live News Feed" && (
          <div className="dashboard-news-feed">
            {news.length > 0 ? (
              news.slice(0, 6).map((article, idx) => (
                <div className="dashboard-news-card" key={idx}>
                  <h3>{article.title}</h3>
                  <p>{article.description?.slice(0, 100)}...</p>
                  <a href={article.link} target="_blank" rel="noopener noreferrer">
                    Read More ‚Üí
                  </a>
                </div>
              ))
            ) : (
              <p>No news found. Please select a country.</p>
            )}
          </div>
        )}

        {activePanel === "AI-Analysis Summary" && (
          <div className="dashboard-ai-panel">
            <h2>üß† AI-Based Analysis</h2>
            <p>{aiSummary}</p>
          </div>
        )}

        {activePanel === "Recent Alerts Panel" && (
          <div className="dashboard-alert-panel">
            <h2>üö® Recent Alerts</h2>
            {recentAlerts.length > 0 ? (
              recentAlerts.map((alert, idx) => (
                <div className="dashboard-alert-card" key={idx}>
                  <h4>{alert.title}</h4>
                  <p>{alert.description?.slice(0, 100)}...</p>
                  <a href={alert.link} target="_blank" rel="noopener noreferrer">
                    Read More ‚Üí
                  </a>
                </div>
              ))
            ) : (
              <p>No major alerts detected in the selected region.</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
