import React, { useState } from "react";
import "./NotificationSettings.css"; // Import the CSS file

const NotificationSettings = () => {
  // State to track toggle status
  const [notifications, setNotifications] = useState({
    email: false,
    sms: false,
    push: false,
    alerts: false,
  });

  // Function to toggle notifications
  const toggleNotification = (type) => {
    setNotifications({ ...notifications, [type]: !notifications[type] });
  };

  return (
    <div className="settings-container">
      {/* Sidebar */}
      <div className="settings-sidebar">
        <h2>System Settings</h2>
        <ul>
          <li><button>AI Parameters</button></li>
          <li><button>Security Settings</button></li>
          <li><button className="active">Notification Preferences</button></li>
          <li><button>Early Warning Dashboard</button></li>
          <li><button>Alert Settings</button></li>
        </ul>
      </div>

      {/* Content Section */}
      <div className="settings-content">
        <h3>Notification Preferences</h3>

        <div className="toggle-container">
          <span>Email Notifications</span>
          <button 
            className={`toggle-btn ${notifications.email ? "on" : ""}`} 
            onClick={() => toggleNotification("email")}
          >
            {notifications.email ? "ON" : "OFF"}
          </button>
        </div>

        <div className="toggle-container">
          <span>SMS Notifications</span>
          <button 
            className={`toggle-btn ${notifications.sms ? "on" : ""}`} 
            onClick={() => toggleNotification("sms")}
          >
            {notifications.sms ? "ON" : "OFF"}
          </button>
        </div>

        <div className="toggle-container">
          <span>Push Notifications</span>
          <button 
            className={`toggle-btn ${notifications.push ? "on" : ""}`} 
            onClick={() => toggleNotification("push")}
          >
            {notifications.push ? "ON" : "OFF"}
          </button>
        </div>

        <div className="toggle-container">
          <span>System Alerts</span>
          <button 
            className={`toggle-btn ${notifications.alerts ? "on" : ""}`} 
            onClick={() => toggleNotification("alerts")}
          >
            {notifications.alerts ? "ON" : "OFF"}
          </button>
        </div>
      </div>
    </div>
  );
};

export default SystemSettings;
