import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import "./AIAnalysis.css"; // Importing CSS

const AIAnalysis = () => {
  const location = useLocation();
  const newsUrl = location.state?.url || "";

  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (newsUrl) {
      fetchAnalysis(newsUrl);
    }
  }, [newsUrl]);

  const fetchAnalysis = async (url) => {
    try {
      const response = await axios.post("http://localhost:5000/analyze", { url });
      setAnalysis(response.data);
    } catch (error) {
      console.error("Error fetching analysis:", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: "20px", color: "white", background: "#121212", minHeight: "100vh" }}>
      <h2 style={{ color: "orange" }}>AI News Analysis</h2>
      <p>Analyzing: <strong>{newsUrl}</strong></p>

      {loading ? (
        <p>Loading AI Analysis...</p>
      ) : analysis ? (
        <div>
          <h3>üì∞ News Authenticity: {analysis.authenticityScore}%</h3>
          <h3>üìä Sentiment: {analysis.sentiment}</h3>
          <h3>üîç Related Articles:</h3>
          <ul>
            {analysis.relatedArticles.map((article, index) => (
              <li key={index}>
                <a href={article.link} target="_blank" rel="noopener noreferrer">
                  {article.title}
                </a>
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <p>Error analyzing news.</p>
      )}
    </div>
  );
};

export default AIAnalysis;
