import React, { useState } from 'react';
import './MyTrainer.css';

const MyTrainer = () => {
  // Initial trainer details
  const [trainer, setTrainer] = useState({
    name: 'Rajesh Kumar',
    experience: '8 years',
    certifications: ['Certified Personal Trainer', 'Yoga Specialist'],
    trainingStyle: 'Yoga and Functional Training',
    availability: 'Monday to Saturday, 7 AM - 8 PM',
  });

  // State to handle reason and form visibility
  const [reason, setReason] = useState('');
  const [showForm, setShowForm] = useState(false);

  // Function to handle trainer change and generate a random trainer
  const changeTrainer = () => {
    const randomTrainer = {
      name: getRandomTrainerName(),
      experience: '5 years',
      certifications: ['Certified Fitness Expert', 'Strength Coach'],
      trainingStyle: 'Weight Training and HIIT',
      availability: 'Tuesday to Sunday, 8 AM - 9 PM',
    };

    setTrainer(randomTrainer); // Update to new trainer's details
    setShowForm(false); // Close the form after submitting
  };

  // Function to close the form without changing the trainer
  const closeForm = () => {
    setShowForm(false); // Close the form and keep old trainer details
  };

  // Function to generate a random trainer's name
  const getRandomTrainerName = () => {
    const names = ['Amit Sharma', 'Priya Desai', 'Suresh Patel', 'Neha Gupta', 'Vikram Singh'];
    const randomIndex = Math.floor(Math.random() * names.length);
    return names[randomIndex];
  };

  return (
    <div className="trainer-container">
      <h2>My Trainer</h2>

      {/* Display trainer details */}
      <div className="trainer-details">
        <h3>Name: {trainer.name}</h3>
        <p>Experience: {trainer.experience}</p>
        <p>Certifications: {trainer.certifications.join(', ')}</p>
        <p>Training Style: {trainer.trainingStyle}</p>
        <p>Availability: {trainer.availability}</p>
      </div>

      {/* Button to change trainer */}
      <button onClick={() => setShowForm(true)} className="change-trainer-btn">
        Change Trainer
      </button>

      {/* Form to change trainer */}
      {showForm && (
        <div className="change-trainer-form">
          <h3>Change Trainer</h3>
          <label htmlFor="reason">Reason for Change:</label>
          <textarea
            id="reason"
            value={reason}
            onChange={(e) => setReason(e.target.value)}
            placeholder="Enter your reason"
          ></textarea>

          <button onClick={changeTrainer} className="submit-btn">
            Submit
          </button>
          <button onClick={closeForm} className="close-btn">
            X
          </button>
        </div>
      )}
    </div>
  );
};

export default MyTrainer;
