import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./DataCollection.css";
import { GoogleGenerativeAI } from "@google/generative-ai";

const API_KEY = "YOUR_GEMINI_API_KEY";

const DataCollection = () => {
  const [sources, setSources] = useState([""]);
  const [loadingIndex, setLoadingIndex] = useState(null);
  const navigate = useNavigate();

  const handleInputChange = (index, value) => {
    const updatedSources = [...sources];
    updatedSources[index] = value;
    setSources(updatedSources);
  };

  const addSource = () => {
    setSources([...sources, ""]);
  };

  const extractNewsBlocks = (html) => {
    const tempDiv = document.createElement("div");
    tempDiv.innerHTML = html;

    const articles = Array.from(tempDiv.querySelectorAll("article, .news, .post, li"));
    const newsItems = [];

    for (let i = 0; i < articles.length && newsItems.length < 5; i++) {
      const title = articles[i].querySelector("h1, h2, h3");
      const body = articles[i].querySelector("p");

      if (title && body) {
        newsItems.push({
          title: title.innerText,
          body: body.innerText
        });
      }
    }

    return newsItems;
  };

  const analyzeArticle = async (genAI, item) => {
    const model = genAI.getGenerativeModel({ model: "gemini-pro" });

    const prompt = `
You are an AI risk and authenticity analyst. Here's a news snippet:

Title: "${item.title}"
Content: "${item.body}"

Respond in this JSON format:
{
  "headline": "${item.title}",
  "summary": "",
  "source_authenticity": "",
  "urgency_level": "",
  "impact_scope": "",
  "recommendation": ""
}
`;

    const result = await model.generateContent(prompt);
    const responseText = result.response.text();
    const cleanJson = responseText.replace(/```json|```/g, "").trim();
    return JSON.parse(cleanJson);
  };

  const handleSelect = async (index) => {
    const selectedUrl = sources[index];

    if (!selectedUrl.startsWith("https://")) {
      alert("Please enter a valid URL!");
      return;
    }

    try {
      setLoadingIndex(index);
      const response = await fetch(selectedUrl);
      const html = await response.text();

      const newsItems = extractNewsBlocks(html);

      if (newsItems.length === 0) {
        alert("No news items found on this page.");
        return;
      }

      const genAI = new GoogleGenerativeAI(API_KEY);
      const allAnalyses = [];

      for (const item of newsItems) {
        const analysis = await analyzeArticle(genAI, item);
        allAnalyses.push(analysis);
      }

      navigate("/ai-analysis", { state: { analysisList: allAnalyses, url: selectedUrl } });
    } catch (err) {
      console.error("Error:", err);
      alert("Something went wrong while processing the page.");
    } finally {
      setLoadingIndex(null);
    }
  };

  return (
    <div className="data-collection">
      <h2 className="title">Data Collection</h2>
      <div className="sources-container">
        {sources.map((source, index) => (
          <div key={index} className="source-input">
            <input
              type="text"
              value={source}
              onChange={(e) => handleInputChange(index, e.target.value)}
              placeholder={`Enter Source ${index + 1}`}
            />
            <button className="select-button" onClick={() => handleSelect(index)}>
              {loadingIndex === index ? "Analyzing..." : "Select"}
            </button>
          </div>
        ))}
      </div>
      <div className="add-source-container">
        <button className="add-source" onClick={addSource}>
          + Add New Source
        </button>
      </div>
    </div>
  );
};

export default DataCollection;
