import React, { useState } from 'react';
import './Profile.css';

const Profile = () => {
  const [activeMenu, setActiveMenu] = useState('myPrograms'); // Default menu option

  // Handle menu item clicks
  const handleMenuClick = (menu) => {
    setActiveMenu(menu);
  };

  return (
    <div className="profile-page">
      {/* Profile Picture */}
      <div className="profile-header">
        <div className="profile-pic-container">
          <img src="/p.jpg" alt="Profile" className="profile-pic" />
        </div>
      </div>

      {/* Left Menu (Hamburger) */}
      <div className="hamburger-menu">
        <div className="menu-toggle" id="menu-toggle">
          <button
            className="open-menu"
            onClick={() => document.getElementById('left-menu').classList.toggle('open')}
          >
            &#8594; {/* Right arrow to open */}
          </button>
        </div>
        <div id="left-menu" className="left-menu">
          <ul>
            <li onClick={() => handleMenuClick('myPrograms')}>My Programs</li>
            <li onClick={() => handleMenuClick('myDietPlans')}>My Diet Plans</li>
            <li onClick={() => handleMenuClick('myArticles')}>My Articles</li>
            <li onClick={() => handleMenuClick('myMedicalHistory')}>My Medical History</li>
            <li onClick={() => handleMenuClick('myProgress')}>My Progress</li>
            <li onClick={() => handleMenuClick('myTrainer')}>My Trainer</li>
            <li onClick={() => handleMenuClick('mySubscription')}>My Subscription</li>
          </ul>
        </div>
      </div>

      {/* Right Content Area */}
      <div className="content-area">
        {activeMenu === 'myPrograms' && (
          <div className="my-programs">
            <img src="/v1.jpg" alt="Program 1" />
            <img src="/v2.jpg" alt="Program 2" />
            <img src="/v3.jpg" alt="Program 3" />
          </div>
        )}
        {/* Add more conditional rendering for other menu options here */}
      </div>
    </div>
  );
};

export default Profile;
