import React, { useState } from 'react';
import './Stories.css';

const Stories = () => {
    // Sample stories data with Indian names, images, and 'kgs' term
    const [stories, setStories] = useState([
        {
            id: 1,
            name: "Ritika Sharma",
            date: "October 12, 2024",
            content: "I lost 12 kgs in 4 months by following a structured workout and diet plan through Health&me. Feeling more energetic!",
            image: "s1.jpeg",
            comments: [],
        },
        {
            id: 2,
            name: "Namit Patel",
            date: "October 10, 2024",
            content: "Health&me guided me to lose 8 kgs in 3 months. I also improved my mental health with meditation and regular yoga.",
            image: "s2.jpeg",
            comments: [],
        },
        {
            id: 3,
            name: "Amrita Desai",
            date: "October 8, 2024",
            content: "I shed 5 kgs of muscle mass in just 2 months by following the personalized training and nutrition plan. Super happy with the results!",
            image: "s3.jpeg",
            comments: [],
        },
        {
            id: 4,
            name: "Sunita Reddy",
            date: "October 6, 2024",
            content: "I managed to shed 10 kgs in 6 months and completely transformed my lifestyle. Health&me has been a life-changer for me!",
            image: "s4.jpeg",
            comments: [],
        },
        {
            id: 5,
            name: "Sandeep Gupta",
            date: "October 4, 2024",
            content: "Through Health&me, I lost 7 kgs in 3 months and have never felt fitter. Their meal plans and workouts are incredibly effective!",
            image: "s5.jpeg",
            comments: [],
        },
        {
            id: 6,
            name: "Venu Gopal Iyer",
            date: "October 2, 2024",
            content: "Thanks to Health&me, I was able to lose 6 kgs in 2 months while managing my diet and doing daily cardio workouts.",
            image: "s6.jpeg",
            comments: [],
        },
    ]);

    // State to handle new comments for each story
    const [newComments, setNewComments] = useState({});

    // Handle adding a comment
    const handleAddComment = (storyId) => {
        if (newComments[storyId]?.trim() !== "") {
            const updatedStories = stories.map(story => {
                if (story.id === storyId) {
                    return {
                        ...story,
                        comments: [...story.comments, newComments[storyId]]
                    };
                }
                return story;
            });
            setStories(updatedStories);
            setNewComments(prev => ({ ...prev, [storyId]: "" })); // Reset comment for this story
        }
    };

    // Handle change in input for specific story
    const handleCommentChange = (storyId, value) => {
        setNewComments(prev => ({ ...prev, [storyId]: value }));
    };

    return (
        <div className="stories-container">
            <h1>Fitness Progress Stories</h1>
            <div className="stories-tiles">
                {stories.map(story => (
                    <div key={story.id} className="story-tile">
                        {/* Story Image */}
                        <img
                            src={`${process.env.PUBLIC_URL}/images/${story.image}`}
                            alt={story.name}
                            className="story-image"
                        />
                        <h3>{story.name}</h3>
                        <p className="story-date">{story.date}</p>
                        <p>{story.content}</p>

                        <div className="comments-section">
                            <h4>Comments:</h4>
                            {story.comments.length > 0 ? (
                                <ul className="comments-list">
                                    {story.comments.map((comment, index) => (
                                        <li key={index}>{comment}</li>
                                    ))}
                                </ul>
                            ) : (
                                <p>No comments yet.</p>
                            )}

                            <div className="comment-input-container">
                                <input
                                    type="text"
                                    placeholder="Write a comment..."
                                    value={newComments[story.id] || ""}
                                    onChange={(e) => handleCommentChange(story.id, e.target.value)}
                                    className="comment-input"
                                />
                                <button
                                    onClick={() => handleAddComment(story.id)}
                                    className="comment-btn"
                                >
                                    Add Comment
                                </button>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Stories;
