import React, { useState } from 'react';
import './MySubscription.css';

const MySubscription = () => {
  const [subscription, setSubscription] = useState({
    plan: 'Premium',
    startDate: '2024-10-01',
    endDate: '2025-10-01',
    status: 'Active',
    features: ['Access to all workout plans', 'Personal diet guidance', 'One-on-one coaching'],
  });

  // Example function to update subscription status dynamically
  const changeSubscriptionStatus = () => {
    setSubscription(prevState => ({
      ...prevState,
      status: 'Expired', // Change to "Expired" when the function is called
    }));
  };

  return (
    <div className="subscription-container">
      <h2>My Subscription</h2>
      <div className="subscription-details">
        <h3>Subscription Plan: {subscription.plan}</h3>
        <p>Start Date: {subscription.startDate}</p>
        <p>End Date: {subscription.endDate}</p>
        <p>Status: <span className={subscription.status === 'Active' ? 'active' : 'expired'}>{subscription.status}</span></p>
        <h4>Features Included:</h4>
        <ul>
          {subscription.features.map((feature, index) => (
            <li key={index}>{feature}</li>
          ))}
        </ul>
        <button onClick={changeSubscriptionStatus} className="change-status-btn">Change Status to Expired</button>
      </div>
    </div>
  );
};

export default MySubscription;
