import React, { useState } from 'react';
import './MySubscription.css';

const MySubscription = () => {
  const [subscription, setSubscription] = useState({
    planName: 'Premium Health Plan',
    planDetails: 'A comprehensive plan covering all fitness and health services.',
    startDate: '2024-10-01', 
    endDate: '2025-10-01',
    amountPaid: 199.99,
    included: ['Personal Trainer', 'Diet Plans', 'Access to Premium Workouts', '24/7 Support'],
    newPlan: null, // Store new selected plan details
    paymentMethod: '',
    paymentSuccess: false, // Track payment success
    newStartDate: null, // Track new start date
    newEndDate: null,   // Track new end date
  });

  const [isChangePlanClicked, setIsChangePlanClicked] = useState(false); // Track button click

  // Available plans to choose from
  const availablePlans = [
    {
      name: 'Basic Health Plan',
      details: 'A basic plan covering essential fitness services.',
      amount: 299.99,
      included: ['Diet Plans', 'Access to Basic Workouts'],
    },
    {
      name: 'Advanced Health Plan',
      details: 'An advanced plan covering additional health services.',
      amount: 349.99,
      included: ['Diet Plans', 'Access to Premium Workouts', '24/7 Support'],
    },
    {
      name: 'Premium Health Plan',
      details: 'A comprehensive plan covering all fitness and health services.',
      amount: 599.99,
      included: ['Personal Trainer', 'Diet Plans', 'Access to Premium Workouts', '24/7 Support'],
    },
  ];

  // Handle plan change selection
  const handlePlanChange = (e) => {
    const selectedPlan = availablePlans.find((plan) => plan.name === e.target.value);
    setSubscription((prevState) => ({
      ...prevState,
      newPlan: selectedPlan,
      paymentMethod: '',
      paymentSuccess: false,
    }));
  };

  // Calculate the price difference between current and new plans
  const calculatePriceDifference = () => {
    if (subscription.newPlan) {
      return subscription.newPlan.amount - subscription.amountPaid;
    }
    return 0;
  };

  // Handle payment method selection
  const handlePaymentMethodChange = (e) => {
    setSubscription((prevState) => ({
      ...prevState,
      paymentMethod: e.target.value,
    }));
  };

  // Simulate payment process and complete the plan change
  const completePayment = () => {
    // Set new plan start date to today and end date to 1 year later
    const newStartDate = new Date().toISOString().split('T')[0]; // Get today's date
    const newEndDate = new Date(new Date().setFullYear(new Date().getFullYear() + 1)).toISOString().split('T')[0]; // One year later

    setSubscription((prevState) => ({
      ...prevState,
      planName: prevState.newPlan.name,
      planDetails: prevState.newPlan.details,
      amountPaid: prevState.newPlan.amount,
      included: prevState.newPlan.included,
      newPlan: null,
      paymentSuccess: true,
      newStartDate: newStartDate, // Set new start date
      newEndDate: newEndDate,     // Set new end date
    }));
    setIsChangePlanClicked(false); // Reset the change plan option
  };

  const priceDifference = calculatePriceDifference();
  const isUpgrade = priceDifference > 0;

  return (
    <div className="subscription-container">
      <h2>My Subscription</h2>
      {subscription.paymentSuccess ? (
        <div className="success-message">
          <p>Plan changed successfully! Your new plan is: {subscription.planName}</p>
          {/* Show new plan details after successful payment */}
          <div className="subscription-details">
            <div className="subscription-field">
              <label>New Plan Name:</label>
              <p>{subscription.planName}</p>
            </div>

            <div className="subscription-field">
              <label>New Plan Details:</label>
              <p>{subscription.planDetails}</p>
            </div>

            <div className="subscription-field">
              <label>Start Date:</label>
              <p>{subscription.newStartDate}</p>
            </div>

            <div className="subscription-field">
              <label>End Date:</label>
              <p>{subscription.newEndDate}</p>
            </div>

            <div className="subscription-field">
              <label>Amount Paid:</label>
              <p>${subscription.amountPaid}</p>
            </div>

            <div className="subscription-field">
              <label>What's Included:</label>
              <ul>
                {subscription.included.map((item, index) => (
                  <li key={index}>{item}</li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      ) : (
        <>
          <div className="subscription-details">
            <div className="subscription-field">
              <label>Current Plan Name:</label>
              <p>{subscription.planName}</p>
            </div>

            <div className="subscription-field">
              <label>Current Plan Details:</label>
              <p>{subscription.planDetails}</p>
            </div>

            <div className="subscription-field">
              <label>Start Date:</label>
              <p>{subscription.startDate}</p>
            </div>

            <div className="subscription-field">
              <label>End Date:</label>
              <p>{subscription.endDate}</p>
            </div>

            <div className="subscription-field">
              <label>Amount Paid:</label>
              <p>${subscription.amountPaid}</p>
            </div>

            <div className="subscription-field">
              <label>What's Included:</label>
              <ul>
                {subscription.included.map((item, index) => (
                  <li key={index}>{item}</li>
                ))}
              </ul>
            </div>
          </div>

          {!isChangePlanClicked && (
            <button className="change-plan-btn" onClick={() => setIsChangePlanClicked(true)}>
              Change My Plan
            </button>
          )}

          {isChangePlanClicked && (
            <div className="change-plan-section">
              <h3>Select New Plan</h3>
              <div className="subscription-field">
                <label>Select New Plan:</label>
                <select onChange={handlePlanChange} value={subscription.newPlan?.name || ''}>
                  <option value="">-- Select a Plan --</option>
                  {availablePlans.map((plan, index) => (
                    <option key={index} value={plan.name}>
                      {plan.name} (${plan.amount})
                    </option>
                  ))}
                </select>
              </div>

              {subscription.newPlan && (
                <div className="new-plan-details">
                  <h4>New Plan Details</h4>
                  <p>{subscription.newPlan.details}</p>
                  <p>Amount: ${subscription.newPlan.amount}</p>

                  <h4>Price Difference</h4>
                  {priceDifference === 0 ? (
                    <p>No additional payment required.</p>
                  ) : isUpgrade ? (
                    <p>You need to pay an additional ${priceDifference.toFixed(2)}.</p>
                  ) : (
                    <p>You will receive a refund of ${Math.abs(priceDifference).toFixed(2)}.</p>
                  )}

                  {isUpgrade && (
                    <div className="payment-method-section">
                      <label>Select Payment Method:</label>
                      <select
                        value={subscription.paymentMethod}
                        onChange={handlePaymentMethodChange}
                      >
                        <option value="">-- Select Payment Method --</option>
                        <option value="Net Banking">Net Banking</option>
                        <option value="Credit Card">Credit Card</option>
                        <option value="Debit Card">Debit Card</option>
                        <option value="UPI Payment">UPI Payment</option>
                      </select>

                      {subscription.paymentMethod && (
                        <button className="pay-btn" onClick={completePayment}>
                          Complete Payment
                        </button>
                      )}
                    </div>
                  )}
                </div>
              )}
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default MySubscription;
