import React, { useState, useEffect } from "react";
import { FaDownload, FaSearch, FaExclamationTriangle, FaCheckCircle } from "react-icons/fa";
import "./AlertsReports.css";

const AlertsReports = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [filter, setFilter] = useState("All");
  const [selectedCountry, setSelectedCountry] = useState("");
  const [selectedState, setSelectedState] = useState("");
  const [selectedCity, setSelectedCity] = useState("");

  const [alerts, setAlerts] = useState([]);
  const [locations, setLocations] = useState({});

  useEffect(() => {
    const sampleAlerts = [
      { id: 1, date: "2025-03-29", country: "USA", state: "California", city: "Los Angeles", level: "High", description: "Political unrest in capital", status: "Ongoing" },
      { id: 2, date: "2025-03-28", country: "India", state: "Maharashtra", city: "Mumbai", level: "Medium", description: "Elections approaching, protests increasing", status: "Monitored" },
      { id: 3, date: "2025-03-27", country: "Germany", state: "Bavaria", city: "Munich", level: "Low", description: "Minor economic fluctuations", status: "Resolved" },
      { id: 4, date: "2025-03-26", country: "Brazil", state: "Sao Paulo", city: "Sao Paulo", level: "High", description: "Riots due to policy change", status: "Ongoing" },
      { id: 5, date: "2025-03-25", country: "UK", state: "England", city: "London", level: "Medium", description: "Media controversy affecting government stability", status: "Monitored" },
    ];

    setAlerts(sampleAlerts);

    // Organizing data for dropdowns
    const locationData = {};
    sampleAlerts.forEach((alert) => {
      if (!locationData[alert.country]) {
        locationData[alert.country] = {};
      }
      if (!locationData[alert.country][alert.state]) {
        locationData[alert.country][alert.state] = [];
      }
      if (!locationData[alert.country][alert.state].includes(alert.city)) {
        locationData[alert.country][alert.state].push(alert.city);
      }
    });

    setLocations(locationData);
  }, []);

  const filteredAlerts = alerts.filter((alert) =>
    (selectedCountry === "" || alert.country === selectedCountry) &&
    (selectedState === "" || alert.state === selectedState) &&
    (selectedCity === "" || alert.city === selectedCity) &&
    (filter === "All" || alert.level === filter) &&
    alert.description.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="alerts-reports-container">
      <h1 className="title">‚ö†Ô∏è Geopolitical Alerts & Reports</h1>

      {/* Search & Filter Controls */}
      <div className="controls">
        <div className="search-container">
          <FaSearch className="search-icon" />
          <input
            type="text"
            placeholder="Search alerts..."
            className="search-bar"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>

        <select className="filter-dropdown" value={filter} onChange={(e) => setFilter(e.target.value)}>
          <option value="All">All Levels</option>
          <option value="High">High Risk</option>
          <option value="Medium">Medium Risk</option>
          <option value="Low">Low Risk</option>
        </select>

        <select className="filter-dropdown" value={selectedCountry} onChange={(e) => { setSelectedCountry(e.target.value); setSelectedState(""); setSelectedCity(""); }}>
          <option value="">Select Country</option>
          {Object.keys(locations).map((country) => (
            <option key={country} value={country}>{country}</option>
          ))}
        </select>

        {selectedCountry && (
          <select className="filter-dropdown" value={selectedState} onChange={(e) => { setSelectedState(e.target.value); setSelectedCity(""); }}>
            <option value="">Select State</option>
            {Object.keys(locations[selectedCountry] || {}).map((state) => (
              <option key={state} value={state}>{state}</option>
            ))}
          </select>
        )}

        {selectedState && (
          <select className="filter-dropdown" value={selectedCity} onChange={(e) => setSelectedCity(e.target.value)}>
            <option value="">Select City</option>
            {locations[selectedCountry]?.[selectedState]?.map((city) => (
              <option key={city} value={city}>{city}</option>
            ))}
          </select>
        )}
      </div>

      {/* Alerts Table */}
      <table className="alerts-table">
        <thead>
          <tr>
            <th>Date</th>
            <th>Country</th>
            <th>State</th>
            <th>City</th>
            <th>Alert Level</th>
            <th>Description</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {filteredAlerts.length > 0 ? (
            filteredAlerts.map((alert) => (
              <tr key={alert.id} className={alert.level.toLowerCase()}>
                <td>{alert.date}</td>
                <td>{alert.country}</td>
                <td>{alert.state}</td>
                <td>{alert.city}</td>
                <td>
                  {alert.level === "High" && <FaExclamationTriangle className="high-icon" />}
                  {alert.level === "Medium" && <FaExclamationTriangle className="medium-icon" />}
                  {alert.level === "Low" && <FaCheckCircle className="low-icon" />}
                  {alert.level}
                </td>
                <td>{alert.description}</td>
                <td>{alert.status}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="7" className="no-alerts">No alerts found.</td>
            </tr>
          )}
        </tbody>
      </table>

      {/* Historical Reports */}
      <div className="historical-reports">
        <h2>üìä Historical Reports</h2>
        <p>Trends in geopolitical risks from previous months.</p>
        <ul>
          <li>üîπ January: Increased media conflicts in Europe.</li>
          <li>üîπ February: Government stability issues in Asia.</li>
          <li>üîπ March: Civil protests increased in Latin America.</li>
        </ul>
      </div>

      {/* Download Button */}
      <button className="download-btn">
        <FaDownload /> Download Reports
      </button>
    </div>
  );
};

export default AlertsReports;
