import React, { useState } from 'react';
import './MyMedicalHistory.css';

const MyMedicalHistory = () => {
  const [formData, setFormData] = useState({
    weight: '',
    age: '',
    bloodGroup: '',
    healthIssues: '',
    description: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
    // You can add form submission logic here
  };

  const handleDiscard = () => {
    setFormData({
      weight: '',
      age: '',
      bloodGroup: '',
      healthIssues: '',
      description: ''
    });
  };

  return (
    <div className="medical-history-form">
      <h2>My Medical History <span role="img" aria-label="edit">✏️</span></h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Weight:</label>
          <input
            type="number"
            name="weight"
            value={formData.weight}
            onChange={handleChange}
            placeholder="kgs"
          />
        </div>
        <div className="form-group">
          <label>Age:</label>
          <input
            type="number"
            name="age"
            value={formData.age}
            onChange={handleChange}
            placeholder="yrs"
          />
        </div>
        <div className="form-group">
          <label>Blood Group:</label>
          <input
            type="text"
            name="bloodGroup"
            value={formData.bloodGroup}
            onChange={handleChange}
            placeholder=""
          />
        </div>
        <div className="form-group wide-input">
          <label>Health Issues or Diseases (if any):</label>
          <input
            type="text"
            name="healthIssues"
            value={formData.healthIssues}
            onChange={handleChange}
            placeholder=""
          />
        </div>
        <div className="form-group wide-input">
          <label>Describe in Detail:</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            placeholder=""
          />
        </div>
        <div className="form-actions">
          <button type="button" className="discard-button" onClick={handleDiscard}>
            Discard
          </button>
          <button type="submit" className="save-button">
            Save Changes
          </button>
        </div>
      </form>
    </div>
  );
};

export default MyMedicalHistory;
