import React, { useState } from 'react';
import { Route, BrowserRouter as Router, Switch, Link } from 'react-router-dom';
import './App.css';

import ProfilePage from './ProfilePage'; // Import ProfilePage Component
import Login from './Login'; // Import Login Component
import Register from './Register'; // Import Register Component

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // State to track login status

  const handleLoginSuccess = () => {
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
  };

  return (
    <Router>
      <div>
        <nav className="navbar">
          <div className="navbar-brand">
            <Link to="/">Health&Me</Link>
          </div>
          <div className="navbar-links">
            <Link to="/about-us">About Us</Link>
            <Link to="/category">Category</Link>
            <Link to="/articles">Articles</Link>
            <Link to="/stories">Stories</Link>
            <Link to="/exercise-tracking">Exercise Tracking</Link>
          </div>
          <div className="navbar-actions">
            <Link to="/login">Login</Link>
            <button className="signup-button" onClick={() => setIsLoggedIn(false)}>
              Sign Up
            </button>
            {/* Profile Button, Hidden until Login/Signup */}
            {isLoggedIn && (
              <Link to="/profile">
                <button className="profile-button">Profile</button>
              </Link>
            )}
          </div>
        </nav>

        <Switch>
          <Route path="/login">
            <Login onLoginSuccess={handleLoginSuccess} />
          </Route>
          <Route path="/register">
            <Register />
          </Route>
          <Route path="/profile">
            {isLoggedIn ? <ProfilePage /> : <Login onLoginSuccess={handleLoginSuccess} />}
          </Route>
          <Route exact path="/">
            <h1>Welcome to the Health&Me App</h1>
          </Route>
        </Switch>
      </div>
    </Router>
  );
};

export default App;
